  indicador_lacuna_id INTEGER(100) UNSIGNED NOT NULL AUTO_INCREMENT,
  indicador_lacuna_cia INTEGER(100) UNSIGNED DEFAULT NULL,
  indicador_lacuna_responsavel INTEGER(100) UNSIGNED DEFAULT NULL,
