INSERT INTO pratica_modelo (pratica_modelo_id, pratica_modelo_nome, pratica_modelo_pontos, pratica_modelo_obs, pratica_modelo_ordem) VALUES 
  (9,'FNQ 2014 1000 pontos',1000,'',3);
INSERT INTO pratica_criterio (pratica_criterio_id, pratica_criterio_modelo, pratica_criterio_numero, pratica_criterio_nome, pratica_criterio_pontos, pratica_criterio_obs, pratica_criterio_resultado, pratica_criterio_orientacao) VALUES 
	(65,9,1,'Liderança',110,'<p>Este Crit&eacute;rio aborda os processos gerenciais relativos &agrave; cultura organizacional e desenvolvimento da gest&atilde;o, governan&ccedil;a, levantamento de interesses das partes interessadas, exerc&iacute;cio da lideran&ccedil;a e an&aacute;lise do desempenho da organiza&ccedil;&atilde;o.</p>\n',0,''),
  (66,9,2,'Estratégias e planos',60,'<p>Este Crit&eacute;rio aborda os processos gerenciais relativos &agrave; formula&ccedil;&atilde;o e &agrave; implementa&ccedil;&atilde;o das estrat&eacute;gias.</p>\n',0,NULL),
  (67,9,3,'Clientes',60,'<p>Este Crit&eacute;rio aborda os processos gerenciais relativos &agrave; an&aacute;lise e desenvolvimento de mercado e ao relacionamento com clientes.</p>\n',0,NULL),
  (68,9,4,'Sociedade',60,'<p>Este Crit&eacute;rio aborda os processos gerenciais relativos &agrave; responsabilidade socioambiental e ao desenvolvimento social.</p>\n',0,NULL),
  (69,9,5,'Informação e conhecimento',60,'<p>Este Crit&eacute;rio aborda os processos gerenciais relativos &agrave;s informa&ccedil;&otilde;es da organiza&ccedil;&atilde;o e ao conhecimento organizacional.</p>\n',0,NULL),
  (70,9,6,'Pessoas',90,'<p>Este Crit&eacute;rio aborda os processos gerenciais relativos aos sistemas de trabalho, &agrave; capacita&ccedil;&atilde;o e desenvolvimento e &agrave; qualidade devida.</p>\n',0,NULL),
  (71,9,7,'Processos',110,'<p>Este Crit&eacute;rio aborda os processos gerenciais relativos aos processos da cadeia de valor, processos relativos a fornecedores e processos econ&ocirc;mico-financeiros.</p>\n',0,NULL),
  (72,9,8,'Resultados',450,'<p>Este crit&eacute;rio aborda a apresenta&ccedil;&atilde;o de resultados estrat&eacute;gicos e operacionais relevantes para a organiza&ccedil;&atilde;o, na forma de indicadores que permitam avaliar, no conjunto, a melhoria dos resultados, o n&iacute;vel de competitividade e o cumprimento de compromissos com requisitos de partes interessadas, nas perspectivas econ&ocirc;mico-financeira, socioambiental e relativas a clientes e mercados, a pessoas e aos processos da cadeia de valor.</p>\n',1,'<ol>\n\t<li style=\"text-align: justify;\">Explicar, resumidamente, o prop&oacute;sito de indicadores cujos nomes n&atilde;o sejam autoexplicativos.</li>\n\t<li style=\"text-align: justify;\">Os referenciais comparativos pertinentes, que forem apresentados, devem ser compat&iacute;veis com os padr&otilde;es gerenciais descritos em 1.4a para permitir a avalia&ccedil;&atilde;o da competitividade.</li>\n\t<li style=\"text-align: justify;\">A competitividade do resultado em um tema pode ser avaliada por meio de indicadores com referenciais comparativos diferentes daqueles utilizados para avaliar a melhoria no mesmo tema.</li>\n\t<li style=\"text-align: justify;\">Os n&iacute;veis de desempenho ou melhorias esperadas, que expressam os principais requisitos de partes interessadas, devem ser compat&iacute;veis com os padr&otilde;es descritos em 1.3C, 3.1 B, 4.1A, 4.2B, 6.3B, 7.1 A, 7.2B E 7.3A.</li>\n\t<li style=\"text-align: justify;\">Podem ser apresentados indicadores, entre os relevantes, sem referencial comparativo pertinente, quando o resultado n&atilde;o for compar&aacute;vel; e sem requisito de parte interessada, quando n&atilde;o refletir necessidades e expectativas de partes interessadas, se eles forem relevantes para avaliar a melhoria.</li>\n\t<li style=\"text-align: justify;\">No caso de unidade aut&ocirc;noma, em indicadores cujo levantamento de resultados &eacute; realizado somente no n&iacute;vel corporativo, demonstrar a contribui&ccedil;&atilde;o da unidade para o resultado alcan&ccedil;ado.</li>\n</ol>\n');
INSERT INTO pratica_item (pratica_item_id, pratica_item_criterio, pratica_item_numero, pratica_item_nome, pratica_item_pontos, pratica_item_obs, pratica_item_oculto, pratica_item_orientacao) VALUES 
  (168,65,1,'Cultura organizacional e desenvolvimento da gestão',28,'<p>Este item aborda os processos gerenciais utilizados para estabelecer valores e princ&iacute;pios organizacionais e padr&otilde;es de conduta, identificar e desenvolver elementos da cultura organizacional, manter a comunica&ccedil;&atilde;o com todos os p&uacute;blicos, estabelecer padr&otilde;es, controlar e melhorar processos gerenciais, e para obter e utilizar as melhores pr&aacute;ticas de gest&atilde;o.</p>\n',0,NULL),
  (169,65,2,'Governança',28,'<p>Este item aborda os processos gerenciais utilizados para estabelecer diretrizes, assegurar a conformidade com requisitos e diretrizes, gerenciar riscos aos quais a organiza&ccedil;&atilde;o est&aacute; sujeita, tomar decis&otilde;es e comunicar fatos importantes, e prestar contas pela dire&ccedil;&aacute;o das a&ccedil;&oacute;es e resultados alcan&ccedil;ados.</p>\n',0,NULL),
  (170,65,3,'Levantamento de interesses e exercício da liderança',27,'<p>Este Item aborda os processos gerenciais utilizados para identificar portes interessadas e levantar seus interesses; identificar necessidades e expectativas dos controladores e demais partes interessadas, traduzindo-as em requisitos; identificar redes de atua&ccedil;&atilde;o, bem como os processos gerenciais da dire&ccedil;&atilde;o empregados para exerc&iacute;cio da sua lideran&ccedil;a e intera&ccedil;&atilde;o com as partes interessadas.&nbsp;&nbsp; &nbsp;</p>\n\n<p>&nbsp;</p>\n',0,NULL),
  (171,65,4,'Análise do desempenho da organização',27,'<p>Este Item aborda os processos gerenciais utilizados para selecionar indicadores e informa&ccedil;&otilde;es, visando &agrave; an&aacute;lise do desempenho, identificar e obter referenciais comparativos, analisar o desempenho operacional e estrat&eacute;gico da organiza&ccedil;&atilde;o, em termos de alcance de metas, competitividade e de atendimento de requisitos de partes interessadas, bem como acompanhar a implementa&ccedil;&atilde;o das decis&otilde;es decorrentes da an&aacute;lise.</p>\n',0,NULL),
  (172,66,1,'Formulação das estratégias',30,'<p>Este Item aborda os processos gerenciais utilizados para analisar o meio ambiente, os mercados, o setor de atua&ccedil;&atilde;o, os ativos intang&iacute;veis e o ambiente interno, e para definir as estrat&eacute;gias e avaliar o modelo de neg&oacute;cio.</p>\n',0,'<ol>\n\t<li style=\"text-align: justify;\">A express&atilde;o &ldquo;formula&ccedil;&atilde;o das estrat&eacute;gias&rdquo; refere-se &agrave; abordagem (formal ou informal) da organiza&ccedil;&atilde;o a fim de se preparar para o futuro. O processo pode utilizar v&aacute;rios tipos diferentes de previs&otilde;es, proje&ccedil;&otilde;es, op&ccedil;&otilde;es, cen&aacute;rios ou outros m&eacute;todos, para se criar uma perspectiva do futuro com o prop&oacute;sito de orientar a tomada de decis&atilde;o e a aloca&ccedil;&atilde;o dos recursos.</li>\n</ol>\n'),
  (173,66,2,'Implementação das estratégias',30,'<p>Este Item aborda os processos gerenciais utilizados para definir indicadores, estabelecer e desdobrar metas e planos de a&ccedil;&atilde;o pelos processos, alocar recursos e acompanhar a implementa&ccedil;&atilde;o dos planos de a&ccedil;&atilde;o.</p>\n',0,NULL),
  (174,67,1,'Análise e desenvolvimento de mercado',30,'<p>Este Item aborda os processos gerenciais utilizados para analisar o mercado, entender as necessidades e expectativas dos clientes, desenvolver e proteger as marcas, tornar os produtos e as marcas conhecidos, bem como para avaliar a imagem perante os clientes e mercados.</p>\n',0,'<ol>\n\t<li style=\"text-align: justify;\">O mercado abordado neste Item refere-se aos mercados que s&atilde;o alvos da coloca&ccedil;&atilde;o de produtos da organiza&ccedil;&atilde;o.</li>\n\t<li style=\"text-align: justify;\">Explicitar, quando aplic&aacute;vel, como as pr&aacute;ticas de gest&atilde;o variam em fun&ccedil;&atilde;o das peculiaridades dos diferentes grupos de clientes e segmentos de mercado.</li>\n\t<li style=\"text-align: justify;\">Os resultados dos principais indicadores de desempenho, bem como os reconhecimentos externos relativos a este Item, s&atilde;o requeridos em 8.3.</li>\n\t<li style=\"text-align: justify;\">Os parceiros com caracter&iacute;sticas de clientes devem ser abordados neste Item, onde aplic&aacute;vel.</li>\n</ol>\n'),
  (175,67,2,'Relacionamento com clientes',30,'<p>Este Item aborda os processos gerenciais utilizados para permitir que o cliente tenha acesso &agrave; organiza&ccedil;&atilde;o, avaliar a satisfa&ccedil;&atilde;o, insatisfa&ccedil;&atilde;o e fidelidade dos clientes, e utilizar as informa&ccedil;&otilde;es para melhoria e desenvolvimento de parcerias.</p>\n',0,'<ol>\n\t<li style=\"text-align: justify;\">Explicitar como as pr&aacute;ticas de gest&atilde;o variam em fun&ccedil;&atilde;o das peculiaridades dos diferentes grupos de clientes e segmentos de mercados e dos parceiros, quando aplic&aacute;vel.</li>\n\t<li style=\"text-align: justify;\">Os resultados dos principais indicadores de desempenho, bem como os reconhecimentos externos relativos a este Item, s&atilde;o requeridos em 8.3.</li>\n</ol>\n'),
  (176,68,1,'Responsabilidade socioambiental',30,'<p>Este Item aborda os processos gerenciais utilizados para traduzir a legisla&ccedil;&atilde;o em requisitos de desempenho, tratar os impactos dos produtos, processos e instala&ccedil;&otilde;es, prevenir acidentes na sociedade e no meio ambiente, propiciar a acessibilidade e promover a&ccedil;&otilde;es volunt&aacute;rias visando ao desenvolvimento sustent&aacute;vel.</p>\n',0,'<ol>\n\t<li style=\"text-align: justify;\">Os resultados dos principais indicadores de desempenho, bem como os reconhecimentos externos relativos a este Item, s&atilde;o requeridos em 8.2.</li>\n</ol>\n'),
  (177,68,2,'Desenvolvimento social',30,'<p>Este Item aborda os processos gerenciais utilizados para identificar as necessidades e expectativas da sociedade a fim de promover o desenvolvimento social, bem como avaliar sua satisfa&ccedil;&atilde;o e utilizar as informa&ccedil;&otilde;es obtidas para a melhoria das a&ccedil;&otilde;es sociais, avalia&ccedil;&atilde;o e zelo pela imagem da organizado perante a sociedade.</p>\n',0,'<ol>\n\t<li style=\"text-align: justify;\">Neste Item, a sociedade inclui as comunidades existentes no entorno das instala&ccedil;&otilde;es da organiza&ccedil;&atilde;o, quando a presen&ccedil;a das instala&ccedil;&otilde;es no local for relevante.</li>\n\t<li style=\"text-align: justify;\">Os resultados dos principais indicadores de desempenho, bem como os reconhecimentos externos relativos a este Item, s&atilde;o requeridos em 8.2.</li>\n</ol>\n'),
  (178,69,1,'Informações da organização',30,'<p>Este Item aborda os processos gerenciais utilizados para identificar necessidades de informa&ccedil;&atilde;o e de seu tratamento, definir, desenvolver, implantar e melhorar os sistemas de informa&ccedil;&atilde;o, integrar as informa&ccedil;&otilde;es da organiza&ccedil;&atilde;o com as partes interessadas externas e disponibiliz&aacute;-las, mantida a seguran&ccedil;a das informa&ccedil;&otilde;es.</p>\n',0,NULL),
  (179,69,2,'Conhecimento da organização',30,'<p>Este Item aborda a implementa&ccedil;&atilde;o de processos gerenciais utilizados para identificar, desenvolver, reter, proteger, difundir e utilizar os conhecimentos necess&aacute;rios para realizar as opera&ccedil;&otilde;es e implementar as estrat&eacute;gias&nbsp; da organiza&ccedil;&atilde;o.</p>\n',0,NULL),
  (180,70,1,'Sistemas de trabalho',30,'<p>Este Item aborda os processos gerenciais utilizados para definir a organiza&ccedil;&agrave;o do trabalho, identificar competências, selecionar e integrar pessoas, avaliar e planejar a melhoria do desempenho, bem como para remunerar e reconhecer as pessoas, buscando o alto desempenho.</p>\n',0,'<ol>\n\t<li style=\"text-align: justify;\">Explicitar, quando aplic&aacute;vel, as pr&aacute;ticas de gest&atilde;o de pessoas que variam em fun&ccedil;&atilde;o dos diferentes grupos de pessoas da for&ccedil;a de trabalho, tais como gestores, t&eacute;cnicos, administrativos e operacionais, horistas e mensalistas, contratados em tempo integral ou parcial, tempor&aacute;rios, aut&ocirc;nomos e contratados de terceiros que trabalham sob coordena&ccedil;&atilde;o direta da organiza&ccedil;&atilde;o.</li>\n\t<li style=\"text-align: justify;\">Os resultados dos principais indicadores de desempenho, bem como os reconhecimentos externos relativos a este Item, s&atilde;o requeridos em 8.4.</li>\n</ol>\n'),
  (181,70,2,'Capacitação e desenvolvimento',30,'<p>Este Item aborda os processos gerenciais utilizados para identificar as necessidades de capacita&ccedil;&aacute;o e desenvolvimento das pessoas, conceber e avaliar a efic&aacute;cia dos programas de capacita&ccedil;&atilde;o e desenvolvimento, identificar e preparar novos l&iacute;deres e sucessores, e desenvolver as pessoas como indiv&iacute;duos, cidad&atilde;os e profissionais.</p>\n',0,'<ol>\n\t<li style=\"text-align: justify;\">Explicitar como as pr&aacute;ticas de gest&atilde;o de pessoas levam em conta as diferentes categorias, tais como os contratados em tempo integral ou parcial, os tempor&aacute;rios, os aut&ocirc;nomos e os contratados de terceiros que trabalham sob coordena&ccedil;&atilde;o direta da organiza&ccedil;&atilde;o, procurando esclarecer, quando necess&aacute;rio, as diferen&ccedil;as existentes.</li>\n\t<li style=\"text-align: justify;\">Os resultados dos principais indicadores de desempenho, bem como os reconhecimentos externos relativos a este Item, s&atilde;o requeridos em 8.4.</li>\n</ol>\n'),
  (182,70,3,'Qualidade de vida',30,'<p>Este Item aborda os processos gerenciais utilizados para tratar os riscos relacionados &agrave; sa&uacute;de e seguran&ccedil;a, identificar as necessidades e expectativas, avaliar o bem-estar, satisfa&ccedil;&atilde;o e comprometimento das pessoas, promover a qualidade de vida e manter um clima organizacional favor&aacute;vel &agrave; criatividade, &agrave; inova&ccedil;&atilde;o&nbsp; e &agrave; excelência no desempenho.</p>\n',0,'<ol>\n\t<li style=\"text-align: justify;\">Explicitar como as pr&aacute;ticas de gest&atilde;o de pessoas levam em conta os diferentes grupos, tais como os contratados em tempo integral ou parcial, os tempor&aacute;rios, os aut&ocirc;nomos e os contratados de terceiros que trabalham sob coordena&ccedil;&atilde;o direta da organiza&ccedil;&atilde;o, esclarecendo, quando necess&aacute;rio, as diferen&ccedil;as existentes.</li>\n\t<li style=\"text-align: justify;\">Os resultados dos principais indicadores de desempenho, bem como os reconhecimentos externos relativos a este Item, s&atilde;o requeridos em 8.4.</li>\n</ol>\n'),
  (183,71,1,'Processos da cadeia de valor',50,'<p>Este Item aborda os processos gerenciais utilizados para identificar requisitos de processos e produtos, projetar, analisar e melhorar produtos e processos, considerando as inova&ccedil;&otilde;es, novas tecnologias e as caracter&iacute;sticas dos produtos e processos de concorrentes ou de outras organiza&ccedil;&otilde;es de referência.</p>\n',0,'<ol>\n\t<li style=\"text-align: justify;\">Os processos da cadeia de valor s&atilde;o requeridos em Perfil P1B(2). Em raz&atilde;o das particularidades da gest&atilde;o dos processos relativos aos fornecedores e dos processos econ&ocirc;mico-financeiros, esses s&atilde;o tratados em destaque nos itens 7.2 e 7.3, respectivamente.</li>\n\t<li style=\"text-align: justify;\">Os produtos citados nas quest&otilde;es deste Item s&atilde;o aqueles gerados, entregues e oferecidos com suporte para os clientes, incluindo servi&ccedil;os associados a essas atividades.</li>\n\t<li style=\"text-align: justify;\">Explicitar, quando aplic&aacute;vel, como as pr&aacute;ticas variam em fun&ccedil;&atilde;o das peculiaridades ou import&acirc;ncia dos processos da cadeia de valor.</li>\n\t<li style=\"text-align: justify;\">Os resultados dos principais indicadores de desempenho, bem como os reconhecimentos externos relativos a este Item, s&atilde;o requeridos em 8.5.</li>\n</ol>\n'),
  (184,71,2,'Processos relativos a fornecedores',30,'<p>Este Item aborda os processos gerenciais utilizados para desenvolver a cadeia de suprimentos, conhecer, qualificar, selecionar, monitorar e avaliar os fornecedores, bem como para comprometê-los com a excelência.</p>\n',0,'<ol>\n\t<li style=\"text-align: justify;\">Explicitar como as pr&aacute;ticas de gest&atilde;o variam em fun&ccedil;&atilde;o das peculiaridades dos diferentes tipos de fornecedores.</li>\n\t<li style=\"text-align: justify;\">Os parceiros mencionados nesse Item representam uma categoria especial de fornecedores, com os quais se estabelecem alian&ccedil;as estrat&eacute;gicas e cujo desempenho &eacute; cr&iacute;tico para o neg&oacute;cio. Podem ser inclu&iacute;dos nesta categoria os distribuidores e revendedores. Os fornecedores dessa categoria s&atilde;o requeridos no Perfil P1D(6).</li>\n\t<li style=\"text-align: justify;\">Os requisitos relativos a fornecedores deste Item aplicam-se tamb&eacute;m &agrave;queles considerados parceiros.</li>\n\t<li style=\"text-align: justify;\">Os resultados dos principais indicadores de desempenho, bem como os reconhecimentos externos relativos a este Item, s&atilde;o requeridos em 8.5.</li>\n</ol>\n'),
  (185,71,3,'Processos econômico-financeiros',30,'<p>Este Item aborda os processos gerenciais&nbsp; utilizados para monitorar os impactos na sustentabilidade econ&ocirc;mico-financeira da organiza&ccedil;&atilde;o, assegurar os recursos para apoiar as opera&ccedil;&otilde;es e as estrat&eacute;gias, quantificar, tratar e monitorar os riscos mais significativos que podem afetar as demonstra&ccedil;&otilde;es financeiras, bem como para elaborar e controlar o or&ccedil;amento.</p>\n',0,'<ol>\n\t<li style=\"text-align: justify;\">Os resultados dos principais indicadores de desempenho bem como os reconhecimentos externos relativos a este Item s&atilde;o requeridos em 8.1.</li>\n</ol>\n'),
  (186,72,1,'Resultados econômico-financeiros',100,'<p>Este Item aborda os resultados econ&ocirc;mico-financeiros da organiza&ccedil;&atilde;o.</p>\n',0,NULL),
  (187,72,2,'Sociais e ambientais',75,'<p>Este Item aborda os resultados sociais e ambientais da organiza&ccedil;&atilde;o.</p>\n',0,NULL),
  (188,72,3,'Relativos a clientes e mercados',100,'<p>Este Item aborda os resultados relativos aos clientes e mercados-alvo da organiza&ccedil;&atilde;o.</p>\n',0,NULL),
  (189,72,4,'Relativos às pessoas',75,'<p>Este Item aborda os resultados relativos &agrave;s pessoas da for&ccedil;a de trabalho.</p>\n',0,NULL),
  (190,72,5,'Relativos aos processos',100,'<p>Este Item aborda os resultados relativos aos produtos, aos processos da cadeia de valor, aos fornecedores e aos demais processos de gest&atilde;o transversais.</p>\n',0,NULL);
INSERT INTO pratica_marcador (pratica_marcador_id, pratica_marcador_item, pratica_marcador_letra, pratica_marcador_texto, pratica_marcador_extra, pratica_marcador_evidencia, pratica_marcador_orientacao) VALUES 
  (941,168,'a','<p>Como s&atilde;o estabelecidos os valores e princ&iacute;pios organizacionais?</p>\n','','<ul>\n\t<li style=\"text-align:justify\">Apresentar os valores e os princ&iacute;pios.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align:justify\">O estabelecimento de valores e princ&iacute;pios organizacionais inclui sua revis&atilde;o e atualiza&ccedil;&atilde;o.</li>\n\t<li style=\"text-align:justify\">Os valores e princ&iacute;pios abrangem aqueles necess&aacute;rios &agrave; promo&ccedil;&atilde;o da excelência, &atilde; cria&ccedil;&atilde;o de valor para todas as partes interessadas e ao desenvolvimento sustent&aacute;vel.</li>\n\t<li style=\"text-align:justify\">A apresenta&ccedil;&atilde;o dos valores e princ&iacute;pios organizacionais inclui, por exemplo, as declara&ccedil;&otilde;es de miss&atilde;o, vis&atilde;o, credos e outras categorias de princ&iacute;pios porventura existentes.</li>\n</ol>\n'),
  (942,168,'b','<p>Como os principais elementos da cultura organizacional s&atilde;o identificados, analisados e desenvolvidos?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar as formas de refor&ccedil;o de elementos favor&aacute;veis e elimina&ccedil;&atilde;o ou minimiza&ccedil;&atilde;o de elementos adversos da cultura organizacional;</li>\n\t<li style=\"text-align:justify\">Destacar de que maneira &eacute; respeitada a diversidade de id&eacute;ias no desenvolvimento da cultura organizacional.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align:justify\">Os elementos da cultura podem abranger comportamentos, pressupostos, preconceitos, s&iacute;mbolos utilizados, formas de organiza&ccedil;&atilde;o do trabalho, rituais, c&oacute;digos ou formas de comunica&ccedil;&atilde;o verbal ou escrita, linguagem empregada e outros, atuantes na organiza&ccedil;&atilde;o.</li>\n\t<li style=\"text-align:justify\">O desenvolvimento de elementos da cultura organizacional identificados visa &agrave; incorpora&ccedil;&atilde;o de comportamentos compat&iacute;veis com a promo&ccedil;&atilde;o da excelência, &agrave; cria&ccedil;&atilde;o de valor para todas as partes interessadas e ao desenvolvimento sustent&aacute;vel.</li>\n\t<li style=\"text-align:justify\">O respeito &atilde; diversidade de ide&iacute;as pode incluir a adapta&ccedil;&atilde;o de padr&otilde;es aos costumes locais; a incorpora&ccedil;&atilde;o de boas pr&aacute;ticas de diversas origens, incluindo de fora do setor e de diferentes nacionalidades ou correntes de pensamento; a participa&ccedil;&atilde;o de profissionais de diferentes faixas et&aacute;rias, forma&ccedil;&otilde;es, n&iacute;veis hier&aacute;rquicos ou gêneros; o est&iacute;mulo &agrave; livre express&atilde;o de opini&otilde;es e qualquer outro mecanismo promotor da diversidade.</li>\n</ol>\n'),
  (943,168,'c','<p>Como a organiza&ccedil;&atilde;o estabelece padr&otilde;es de conduta e busca assegurar um relacionamento &eacute;tico no ambiente em que opera?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar a forma de preven&ccedil;&atilde;o de desvios de conduta envolvendo a organiza&ccedil;&atilde;o</li>\n\t<li style=\"text-align:justify\">Destacar a forma de tratamento das manifesta&ccedil;&otilde;es relativas &agrave; conduta &eacute;tica e de retorno aos envolvidos.</li>\n</ul>\n','<ul>\n\t<li style=\"text-align:justify\">Apresentar os canais de comunica&ccedil;&atilde;o dispon&iacute;veis para receber reclama&ccedil;&otilde;es, den&uacute;ncias e sugest&otilde;es relativas &agrave; conduta, envolvendo a organiza&ccedil;&atilde;o;</li>\n\t<li style=\"text-align:justify\">Citar os c&oacute;digos de conduta emitidos.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align:justify\">As formas de preven&ccedil;&atilde;o de desvios de conduta abrangem os principais processos e relacionamentos em que h&aacute; risco de ocorrência de ass&eacute;dio n&atilde;o &eacute;tico, corrup&ccedil;&atilde;o e fraudes.</li>\n\t<li style=\"text-align:justify\">Os interlocutores do ambiente em que a organiza&ccedil;&atilde;o opera, que est&atilde;o envolvidos nas quest&otilde;es &eacute;ticas, abrangem partes interessadas, redes importantes, concorrentes e outros interlocutores com os quais a organiza&ccedil;&atilde;o precisa se relacionar.</li>\n</ol>\n'),
  (944,168,'d','<p>Como os temas para o desenvolvimento da cultura organizacional e para o engajamento das pessoas s&atilde;o comunicados &agrave; for&ccedil;a de trabalho e a outros p&uacute;blicos?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar as formas de comunica&ccedil;&atilde;o de valores e princ&iacute;pios organizacionais, padr&otilde;es de conduta, estrat&eacute;gias e objetivos, metas e melhoria de resultados alcan&ccedil;ados, &agrave; for&ccedil;a de trabalho e a outros p&uacute;blicos-ah/o da comunica&ccedil;&atilde;o;</li>\n\t<li style=\"text-align:justify\">Destacar de que forma &eacute; avaliado o entendimento dos temas pela for&ccedil;a de trabalho e outros p&uacute;blicos- alvo da comunica&ccedil;&atilde;o.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align:justify\">Os outros p&uacute;blicos mencionados nesta quest&atilde;o abrangem, quando aplic&aacute;vel, partes interessadas pertinentes e redes importantes, conforme o tema comunicado.</li>\n</ol>\n'),
  (945,168,'e','<p>Como s&atilde;o estabelecidos os principais padr&otilde;es para os processos gerenciais e como &eacute; verificado o seu cumprimento?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar a forma de implementar as a&ccedil;&otilde;es corretivas.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align:justify\">Os padr&otilde;es para os processos gerenciais, tamb&eacute;m chamados de padr&otilde;es gerenciais, determinam o funcionamento das pr&aacute;ticas de gest&atilde;o adotadas pela organiza&ccedil;&atilde;o para atender aos processos gerenciais requeridos.</li>\n\t<li style=\"text-align:justify\">A verifica&ccedil;&atilde;o do cumprimento dos padr&otilde;es gerenciais faz parte do ciclo de controle que integra o PDCL, preconizado pelo Diagrama do Ciclo da Gest&atilde;o (Capitulo IX).</li>\n</ol>\n'),
  (946,168,'f','<p>Como os processos gerenciais s&atilde;o aperfei&ccedil;oados?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar a forma de promover melhorias nos processos gerenciais por meio do aprendizado e da inova&ccedil;&atilde;o.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align:justify\">O aperfei&ccedil;oamento dos processos gerenciais faz parte do ciclo de aprendizado que integra o PDCL, preconizado pelo Diagrama do Ciclo da Gest&atilde;o (Capitulo 1).</li>\n\t<li style=\"text-align:justify\">Melhorias e inova&ccedil;&otilde;es recentes introduzidas no sistema de gest&atilde;o da organizo&ccedil;&atilde;o devem ser exemplificadas, sempre que poss&iacute;vel, ao longo da descri&ccedil;&atilde;o das pr&aacute;ticas de gest&atilde;o relativas aos processos gerenciais solicitados nos crit&eacute;rios de 1 a 7, a fim de evidenciar o aprendizado.</li>\n</ol>\n'),
  (947,168,'g','<p>Como s&atilde;o obtidas e utilizadas as informa&ccedil;&otilde;es sobre boas pr&aacute;ticas de gest&atilde;o de organiza&ccedil;&otilde;es de referência?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar os crit&eacute;rios de sele&ccedil;&atilde;o das organiza&ccedil;&otilde;es de referência.</li>\n</ul>\n','<ul>\n\t<li style=\"text-align:justify\">Citar as principais melhorias recentes implementadas nos processos gerenciais, em decorrência da utiliza&ccedil;&atilde;o das boas pr&aacute;ticas obtidas.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align:justify\">A busca e a incorpora&ccedil;&atilde;o das boas pr&aacute;ticas de gest&atilde;o fazem parte do ciclo de aprendizado que integra o PDCL, preconizado pelo Diagrama do Ciclo da Gest&atilde;o (Cap&iacute;tulo 1).</li>\n\t<li style=\"text-align:justify\">As organiza&ccedil;&otilde;es de referência incluem aquelas de dentro e de fora do ramo ou setor de atua&ccedil;&atilde;o da organiza&ccedil;&atilde;o.</li>\n\t<li style=\"text-align:justify\">Melhorias introduzidas no sistema de gest&atilde;o da organiza&ccedil;&atilde;o, em conseq&uuml;ência da investiga&ccedil;&atilde;o de boas pr&aacute;ticas de outras organiza&ccedil;&otilde;es, tamb&eacute;m devem ser citadas, sempre que poss&iacute;vel, ao longo da descri&ccedil;&atilde;o das pr&aacute;ticas de gest&atilde;o relativas aos processos gerenciais solicitados nos Crit&eacute;rios de 1 a 7, a fim de evidenciar a utiliza&ccedil;&atilde;o das boas pr&aacute;ticas de gest&atilde;o obtidas.</li>\n</ol>\n'),
  (948,169,'a','<p>Como s&atilde;o estabelecidas as diretrizes que a organiza&ccedil;&atilde;o deve observar?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar o envolvimento dos controladores da organiza&ccedil;&atilde;o na defini&ccedil;&atilde;o das diretrizes.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">As diretrizes que a organiza&ccedil;&atilde;o deve observar, al&eacute;m das exigências legais e regulamentares, s&atilde;o aquelas utilizadas pelos controladores para definir limites e orientar a atua&ccedil;&atilde;o dos administradores e da for&ccedil;a de trabalho na tomada de decis&atilde;o. Podem incluir, por exemplo, regras estatut&aacute;rias, pol&iacute;ticas, cl&aacute;usulas de contratos de gest&atilde;o, regulamentos corporativos e compromissos estabelecidos com partes interessadas ou redes importantes.</li>\n</ol>\n'),
  (949,169,'b','<p>Como &eacute; assegurada a conformidade da organiza&ccedil;&atilde;o com os requisitos legais e regulamentares aplic&aacute;veis e com as diretrizes estabelecidas?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar as formas utilizadas para prevenir e investigar desvios e implementar a&ccedil;&otilde;es corretivas quando necess&aacute;rio;</li>\n\t<li style=\"text-align:justify\">Destacar o envolvimento dos controladores da organiza&ccedil;&atilde;o na verifica&ccedil;&atilde;o da conformidade;</li>\n\t<li style=\"text-align:justify\">Destacar a forma de assegurar a independência dos mecanismos de verifica&ccedil;&atilde;o de conformidade.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">Os requisitos legais e regulamentares aplic&aacute;veis, explicitados pela sociedade , gero, devem considerar as regi&otilde;es de atua&ccedil;&atilde;o e s&atilde;o identificados por meio do processo solicitado em 4.1A.</li>\n</ol>\n'),
  (950,169,'c','<p>Como s&atilde;o identificados e tratados os riscos aos quais a organiza&ccedil;&atilde;o est&aacute; sujeita?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar a forma de classificar, definir a relev&acirc;ncia e monitorar os riscos.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">Os riscos aos quais a organiza&ccedil;&atilde;o est&aacute; sujeita s&atilde;o aqueles que amea&ccedil;am a consecu&ccedil;&atilde;o dos objetivos estrat&eacute;gicos e do neg&oacute;cio &agrave; luz das incertezas do mercado e do setor de atua&ccedil;&atilde;o da organiza&ccedil;&atilde;o, do ambiente macroecon&ocirc;mico e dos pr&oacute;prios processos da organiza&ccedil;&atilde;o.</li>\n\t<li style=\"text-align: justify;\">A identifica&ccedil;&atilde;o de riscos deve considerar tamb&eacute;m aqueles que possam vir a afetar as demonstra&ccedil;&otilde;es financeiras da organiza&ccedil;&atilde;o e aqueles associados a dificuldades criadas por organiza&ccedil;&otilde;es, pessoas ou redes, cujos interesses se op&otilde;em aos da organiza&ccedil;&atilde;o, corno, por exemplo: &nbsp;concorrência desleal ou hostil; organiza&ccedil;&otilde;es anti&eacute;ticas ou irrespons&aacute;veis; crime organizado; fornecedores ou clientes com m&aacute;-f&eacute; e outras.</li>\n\t<li style=\"text-align: justify;\">Os riscos aos quais a organiza&ccedil;&atilde;o est&aacute; sujeita podem ser classificados em fun&ccedil;&atilde;o da sua origem ou tipo &mdash; por exemplo, risco de sa&uacute;de e seguran&ccedil;a, risco ambiental, risco financeiro, risco lego, risco do neg&oacute;cio, risco tecnol&oacute;gico, risco operacional e risco regulat&oacute;rio, entre outros.</li>\n\t<li style=\"text-align: justify;\">Os riscos aos quais a organiza&ccedil;&atilde;o est&aacute; sujeita podem ter sua relev&acirc;ncia definida pela combina&ccedil;&atilde;o da probabilidade de ocorrência, da intensidade do impacto e da tendência de agravamento ao longo do tempo.</li>\n\t<li style=\"text-align: justify;\">Aforres de tratamento pode incluir a aceita&ccedil;&atilde;o da exposi&ccedil;&atilde;o atual.</li>\n</ol>\n'),
  (951,169,'d','<p>Como as principais decis&otilde;es s&atilde;o tomadas, comunicadas aos envolvidos e implementadas?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar a forma de assegurar transparência e envolvimento dos principais interessados nos temas tratados;</li>\n\t<li style=\"text-align:justify\">Destacar o papel dos administradores na tomada de decis&atilde;o;</li>\n\t<li style=\"text-align:justify\">Destacar a forma utilizada para adequar a comunica&ccedil;&atilde;o a cada tipo de p&uacute;blico envolvido;</li>\n\t<li style=\"text-align:justify\">Destacar de que maneira os sistemas de informa&ccedil;&atilde;o proporcionam agilidade na tomada de decis&atilde;o em todos os n&iacute;veis da organiza&ccedil;&atilde;o.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">A tornada de decis&atilde;o abrange os m&eacute;todos coordenados para deliberar e decidir dire&ccedil;oes a seguir e a&ccedil;&otilde;es a realizar, desde o n&iacute;vel de governan&ccedil;a at&eacute; o n&iacute;vel operacional, em alinhamento &agrave;s estrat&eacute;gias e objetivos da organiza&ccedil;&atilde;o.</li>\n\t<li style=\"text-align: justify;\">A comunica&ccedil;&atilde;o deve ser adequada a cada tipo de p&uacute;blico envolvido, em fun&ccedil;&atilde;o das decis&otilde;es tomadas.</li>\n\t<li style=\"text-align: justify;\">A implementa&ccedil;&atilde;o das decis&otilde;es contempla as formas de organizar as a&ccedil;&otilde;es necess&aacute;rias e de seu acompanhamento.</li>\n\t<li style=\"text-align: justify;\">O apoio de sistemas de informa&ccedil;&atilde;o propicia a decis&atilde;o com base em indicadores de desempenho extra&iacute;dos das bases de dados e em mecanismos de alerta antecipado, que detectam situa&ccedil;&otilde;es an&ocirc;malas ou fora do padr&atilde;o esperado e alertam preventivamente as &aacute;reas e n&iacute;veis de tomada de decis&atilde;o pertinentes sobre tais situa&ccedil;&otilde;es e sua evolu&ccedil;&atilde;o.</li>\n</ol>\n'),
  (952,169,'e','<p>Como os fatos importantes s&atilde;o prontamente comunicados &agrave; sociedade e &agrave;s demais partes interessadas?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar os crit&eacute;rios utilizados para defini&ccedil;&atilde;o do tipo de fato que deve ser considerado importante para ser comunicado.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">O tipo de fato importante pode variar de acordo com a parte interessada e a natureza do impacto sobre a mesma.</li>\n\t<li style=\"text-align: justify;\">Podem ser considerados fatos importantes a serem comunicados, especificamente &agrave; sociedade, os impactos sociais e ambientais dos produtos, processos e instala&ccedil;&otilde;es, assim como as por cor, as a&ccedil;&otilde;es e os resultados relativos &agrave; responsabilidade socioambiental.</li>\n</ol>\n'),
  (953,169,'f','<p>Como a dire&ccedil;&atilde;o presta contas das suas a&ccedil;&otilde;es e resultados alcan&ccedil;ados aos controladores da organiza&ccedil;&atilde;o e &agrave;s demais partes interessadas relevantes?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar a forma utilizada para assegurar a autenticidade e suficiência das informa&ccedil;&otilde;es relativas &agrave; presta&ccedil;&atilde;o de contas;</li>\n\t<li style=\"text-align:justify\">Destacar os m&eacute;todos utilizados para controlar os atos da dire&ccedil;&atilde;o.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">A composi&ccedil;&atilde;o nominal da dire&ccedil;&atilde;o da organiza&ccedil;&atilde;o, incluindo as principais lideran&ccedil;as, &eacute; requerida no Perfil P5(1) &mdash; Estrutura organizacional.</li>\n\t<li style=\"text-align: justify;\">O controle sobre os atos da dire&ccedil;&atilde;o visa verificar se esses est&atilde;o ocorrendo de acordo coro os limites de delega&ccedil;&atilde;o pr&eacute;-estabelecidos em estatutos, contratos de gest&atilde;o, defini&ccedil;&otilde;es de n&iacute;veis de al&ccedil;ada ou documentos afins.</li>\n</ol>\n'),
  (954,170,'a','<p>Como s&atilde;o identificadas as partes interessadas relevantes e como s&atilde;o conhecidos os seus interesses?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar o crit&eacute;rio utilizado para defini&ccedil;&atilde;o das partes interessadas relevantes;</li>\n\t<li style=\"text-align:justify\">Destacar a forma de proteger os direitos das partes interessadas relevantes;</li>\n\t<li style=\"text-align:justify\">Destacar a forma de assegurar a equidade entre s&oacute;cios, mantenedores ou instituidores.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align:justify\">As partes interessadas relevantes s&atilde;o aquelas cujas necessidades e expectativas ou interesses devem ser identificadas e compreendidas plenamente.</li>\n\t<li style=\"text-align:justify\">O levantamento dos interesses das partes interessadas relevantes e a prote&ccedil;&atilde;o de seus direitos visam maximizar a sua satisfa&ccedil;&atilde;o de forma equilibrada e a sustentabilidade da organiza&ccedil;&atilde;o.</li>\n\t<li style=\"text-align:justify\">As principais partes interessadas e respectivas necessidades e expectativas s&atilde;o requeridas no Perfil P1C(1).</li>\n</ol>\n'),
  (955,170,'b','<p>Como as necessidades e expectativas dos controladores e demais partes interessadas n&atilde;o tradicionais s&atilde;o identificadas, analisadas e traduzidas em requisitos de desempenho?</p>\n','','<ul>\n\t<li style=\"text-align:justify\">Apresentar os principais requisitos e respectivos indicadores de desempenho associados aos controladores e demais partes interessadas n&atilde;o tradicionais.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align:justify\">As partes interessadas n&atilde;o tradicionais n&atilde;o incluem clientes, sociedade, for&ccedil;a de trabalho e fornecedores. A identifica&ccedil;&atilde;o das necessidades e expectativas dessas &uacute;ltimas, bem como a tradu&ccedil;&atilde;o em requisitos de desempenho, &eacute; solicitada, respectivamente, nos itens 3.1 B. 4.2A, 6.3B e 7.2B.</li>\n</ol>\n'),
  (956,170,'c','<p>Como s&atilde;o identificadas e desenvolvidas redes de organiza&ccedil;&otilde;es ou de pessoas que possam influenciar no êxito das estrat&eacute;gias e no alcance sustentado dos objetivos da organiza&ccedil;&atilde;o?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar a forma de atua&ccedil;&atilde;o da organiza&ccedil;&atilde;o nas redes importantes que a beneficiam ou prejudicam.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align:justify\">A identifica&ccedil;&atilde;o de redes deve abranger as atuais e potenciais, dentro ou que envolvem a organiza&ccedil;&atilde;o, e que ela julgue ser as mais importantes para se relacionar, fomentar, formar ou delas se proteger.</li>\n\t<li style=\"text-align:justify\">Exemplos de redes que envolvem a organiza&ccedil;&atilde;o incluem as redes associativas, de produtores, de clientes, de usu&aacute;rios, de fornecedores. de parceiros, de distribuidores, de consumidores, de apoiadores, sociais e outras, formais ou informais.</li>\n\t<li style=\"text-align:justify\">Exemplos de redes que atuam dentro da organiza&ccedil;&atilde;o incluem as redes de especialistas, de facilitadores, de comunicadores, de usu&aacute;rios, de apoio m&uacute;tuo, de &aacute;reas afins e outras, formais e informais.</li>\n</ol>\n'),
  (957,170,'d','<p>Como a dire&ccedil;&atilde;o exerce a lideran&ccedil;a e interage com as demais partes interessadas e redes mais importantes?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar de que forma a dire&ccedil;&atilde;o exerce sua lideran&ccedil;a junto &agrave; for&ccedil;a de trabalho.</li>\n</ul>\n','<ul>\n\t<li style=\"text-align:justify\">Citar os objetivos das principais intera&ccedil;&otilde;es realizadas com as partes interessadas e redes mais importantes.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">O exerc&iacute;cio da lideran&ccedil;a e a intera&ccedil;&atilde;o com as partes interessadas e redes mais importantes visam engajar pessoas e organiza&ccedil;&otilde;es, promovendo o alinhamento de interesses e buscando apoio para o êxito das estrat&eacute;gias e o alcance sustentado dos objetivos da organiza&ccedil;&atilde;o. Visam tamb&eacute;m harmonizar os interesses das partes interessadas, buscando a sua satisfa&ccedil;&atilde;o.</li>\n\t<li style=\"text-align: justify;\">O exerc&iacute;cio da lideran&ccedil;a e a intera&ccedil;&atilde;o com partes interessadas e redes mais importantes podem incluir aquelas realizadas por interm&eacute;dio da estrutura de lideran&ccedil;a adotada.</li>\n</ol>\n'),
  (958,171,'a','<p>Como s&atilde;o selecionados os indicadores e demais informa&ccedil;&otilde;es que ser&atilde;o utilizados na an&aacute;lise, pela dire&ccedil;&atilde;o, do desempenho estrat&eacute;gico e operacional da organiza&ccedil;&atilde;o?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar os crit&eacute;rios utilizados para a sele&ccedil;&atilde;o dos indicadores de desempenho e demais informa&ccedil;&otilde;es utilizadas na an&aacute;lise do desempenho estrat&eacute;gico e operacional.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">O desempenho operacional refere-se ao desempenho dos processos da cadeia de valor citados no Perfil P1B(2).</li>\n</ol>\n'),
  (959,171,'b','<p>Como s&atilde;o identificados e obtidos os referenciais comparativos necess&aacute;rios para analisar a competitividade da organiza&ccedil;&atilde;o?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar os crit&eacute;rios utilizados para definir quais s&atilde;o os resultados mais importantes a serem comparados;</li>\n\t<li style=\"text-align:justify\">Destacar os crit&eacute;rios utilizados para definir a pertinência de referenciais comparativos utilizados pela organiza&ccedil;&atilde;o.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">Os referenciais comparativos s&atilde;o aqueles necess&aacute;rios para apoiar a an&aacute;lise da competitividade da organiza&ccedil;&atilde;o, tanto no &acirc;mbito operacional quanto no estrat&eacute;gico.</li>\n\t<li style=\"text-align: justify;\">A obten&ccedil;&atilde;o dos referenciais comparativos, no mercado ou setor de atua&ccedil;&atilde;o, pode abrangera regi&atilde;o de atua&ccedil;&atilde;o da organiza&ccedil;&atilde;o ou, quando se desejar conhecer os n&iacute;veis dos melhores da classe, as regi&otilde;es onde o setor &eacute; mais desenvolvido ou o mundo inteiro.</li>\n\t<li style=\"text-align: justify;\">As identifica&ccedil;&otilde;es dos referenciais comparativos, utilizados na demonstra&ccedil;&atilde;o de resultados no Crit&eacute;rio 8, devem ser coerentes com os crit&eacute;rios de defini&ccedil;&atilde;o de sua pertinência aqui informados. Para identificar o referencial comparativo utilizado, deve ser informada na demonstra&ccedil;&atilde;o de resultados, no Crit&eacute;rio 8, a caracter&iacute;stica ou tipo de m&eacute;dia relevante, do setor ou mercado, para avaliar a competitividade; nome dos concorrentes; nome da organiza&ccedil;&atilde;o de referência; nome de &iacute;ndice ou taxa referenda ou outros.</li>\n</ol>\n'),
  (960,171,'c','<p>Como a dire&ccedil;&atilde;o analisa o desempenho estrat&eacute;gico e operacional da organiza&ccedil;&atilde;o?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar as t&eacute;cnicas utilizadas para agrega&ccedil;&atilde;o e an&aacute;lise dos resultados, incluindo a forma de utiliza&ccedil;&atilde;o das vari&aacute;veis do ambiente interno e externo para contextualizar a an&aacute;lise e tomada de decis&atilde;o;</li>\n\t<li style=\"text-align:justify\">Destacar as formas de utiliza&ccedil;&atilde;o, na avalia&ccedil;&atilde;o do desempenho, de metas, referenciais comparativos e principais requisitos de partes interessadas.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">O desempenho operacional refere-se ao desempenho dos processos da cadeia de valor citadas na Perfil P1B(2).</li>\n\t<li style=\"text-align: justify;\">Na apresenta&ccedil;&atilde;o de resultados no Crit&eacute;rio 8, que expressem os principais requisitos de panes interessadas, coerentes com as suas necessidades e expectativas mencionadas ao longo do Perfil, deve-se demonstrar que esses requisitos estejam sendo atendidos.</li>\n</ol>\n'),
  (961,171,'d','<p>Como &eacute; acompanhada a implementa&ccedil;&atilde;o das decis&otilde;es decorrentes da an&aacute;lise do desempenho da organiza&ccedil;&atilde;o?</p>\n','<ul>\n\t<li style=\"text-align: justify;\">Destacar a forma utilizada pela dire&ccedil;&atilde;o para acompanhar a implementa&ccedil;&atilde;o das decis&otilde;es.</li>\n</ul>\n','',''),
  (962,172,'a','<p>Como &eacute; analisado o macroambiente de atua&ccedil;&atilde;o da organiza&ccedil;&atilde;o e suas tendências?</p>\n','','<ul>\n\t<li style=\"text-align:justify\">Citar as principais informa&ccedil;&otilde;es utilizadas e as respectivas fontes.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">A an&aacute;lise do macroambiente abrange a investiga&ccedil;&atilde;o de for&ccedil;as impulsoras e restritivas, atuais e emergentes, decorrentes de aspectos conjunturais das regi&otilde;es de atua&ccedil;&atilde;o, tais como sociais, pol&iacute;ticos, legais, econ&ocirc;micos e tecnol&oacute;gicos.</li>\n</ol>\n'),
  (963,172,'b','<p>Como s&atilde;o analisadas as caracter&iacute;sticas do setor de atua&ccedil;&atilde;o da organiza&ccedil;&atilde;o e suas tendências?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar de que forma os benef&iacute;cios ou dificuldades associadas &agrave;s redes importantes para a organiza&ccedil;&atilde;o s&atilde;o consideradas na an&aacute;lise;</li>\n\t<li style=\"text-align:justify\">Destacar de que forma as tecnologias relevantes necess&aacute;rias para o neg&oacute;cio s&atilde;o identificadas e avaliadas.</li>\n</ul>\n','<ul>\n\t<li style=\"text-align:justify\">Citar as principais informa&ccedil;&otilde;es utilizadas e as respectivas fontes.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align:justify\">A an&aacute;lise das caracter&iacute;sticas do setor de atua&ccedil;&atilde;o engloba a investiga&ccedil;&atilde;o de for&ccedil;as impulsoras e restritivas, atuais e emergentes, decorrentes de aspectos relativos ao ambiente operacional da organiza&ccedil;&atilde;o, inerentes ao seu ramo. tais como as caracter&iacute;sticas dos principais produtores da cadeia, incluindo for&ccedil;as concorrenciais e seus diferenciais competitivos, dos formadores de opini&atilde;o e influenciadores setoriais, e for&ccedil;as atuantes no setor como um todo.</li>\n\t<li style=\"text-align:justify\">As tecnologias relevantes mencionadas nesta quest&atilde;o s&atilde;o aquelas necess&aacute;rias para o bom desempenho de todos os processos da cadeia de valor, bem como dos processos gerenciais.</li>\n</ol>\n'),
  (964,172,'c','<p>Como s&atilde;o analisados os mercados de atua&ccedil;&atilde;o da organiza&ccedil;&atilde;o e suas tendências?</p>\n','','<ul>\n\t<li style=\"text-align:justify\">Citar as principais informa&ccedil;&otilde;es utilizadas e as respectivas fontes.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">A an&aacute;lise dos mercados de atua&ccedil;&atilde;o abrange a investiga&ccedil;&atilde;o de for&ccedil;as impulsoras e restritivas, atuais e emergentes, relativas &agrave; competi&ccedil;&atilde;o por clientes, oportunidades e recursos, tais como o mercado-alvo de coloca&ccedil;&atilde;o de produtos, o mercado de trabalho, o mercado financeiro e de capitais, o mercado de fornecimento, o mercado de atua&ccedil;&atilde;o socioambiental.</li>\n</ol>\n'),
  (965,172,'d','<p>Como s&atilde;o identificados e avaliados os ativos intang&iacute;veis mais importantes para a organiza&ccedil;&atilde;o?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar os crit&eacute;rios utilizados para defini&ccedil;&atilde;o dos ativos intang&iacute;veis mais importantes para a organiza&ccedil;&atilde;o;</li>\n\t<li style=\"text-align:justify\">Destacar de que forma os ativos intang&iacute;veis mais importantes s&atilde;o desenvolvidos;</li>\n\t<li style=\"text-align:justify\">Destacar a maneira de proteger os ativos intang&iacute;veis mais importantes.</li>\n</ul>\n','<ul>\n\t<li style=\"text-align:justify\">Citar os ativos intang&iacute;veis mais importantes.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">Os ativos intang&iacute;veis mais importantes para a organiza&ccedil;&atilde;o s&atilde;o aqueles que mais agregam valor ao neg&oacute;cio, gerando diferenciais competitivos para a organiza&ccedil;&atilde;o;</li>\n\t<li style=\"text-align: justify;\">O desenvolvimento dos ativos intang&iacute;veis marca, conhecimento e tecnologia &eacute; abordado, respectivamente, nos itens 3.1 C, 5.2 B e 7.1 G. n&atilde;o precisando ser abordado nesse Item.</li>\n</ol>\n'),
  (966,172,'e','<p>Como &eacute; analisado o ambiente interno da organiza&ccedil;&atilde;o?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar de que forma as informa&ccedil;&otilde;es sobre os riscos aos quais a organiza&ccedil;&atilde;o est&aacute; sujeita, os elementos culturais a serem desenvolvidos e as competências essenciais s&atilde;o considerados na an&aacute;lise.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">Os riscos aos quais a organiza&ccedil;&atilde;o est&aacute; sujeita s&atilde;o geridos em 1.2C.</li>\n</ol>\n'),
  (967,172,'f','<p>Como s&atilde;o avaliadas as alternativas decorrentes da an&aacute;lise estrat&eacute;gica e definidas as estrat&eacute;gias a serem adotadas pela organiza&ccedil;&atilde;o?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar de que forma os requisitos das partes interessadas, bem como o desenvolvimento sustent&aacute;vel, s&atilde;o considerados e inseridos nas estrat&eacute;gias;</li>\n\t<li style=\"text-align:justify\">Destacar de que forma a tecnologia da informa&ccedil;&atilde;o &eacute; utilizada para alavancar o neg&oacute;cio.</li>\n</ul>\n','<ul>\n\t<li style=\"text-align:justify\">Apresentar as principais estrat&eacute;gias e objetivos da organiza&ccedil;&atilde;o.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">O desenvolvimento sustent&aacute;vel nesta quest&atilde;o engloba tamb&eacute;m o bem-estar da sociedade e a preserva&ccedil;&atilde;o e melhoria do meio ambiente.</li>\n</ol>\n'),
  (968,172,'g','<p>Como &eacute; avaliado o modelo de neg&oacute;cio em conson&acirc;ncia com a defini&ccedil;&atilde;o das estrat&eacute;gias?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar de que forma s&atilde;o considerados a entrada e a sa&iacute;da em neg&oacute;cios e mercados, o desenvolvimento de novas linhas de produtos ou sua retirada do mercado, o estabelecimento de parcerias e a reorganiza&ccedil;&atilde;o dos processos da cadeia de valor.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">A avalia&ccedil;&atilde;o do modelo de neg&oacute;cio visa potencializar o êxito das estrat&eacute;gias.</li>\n</ol>\n'),
  (969,173,'a','<p>Como s&atilde;o definidos os indicadores para a avalia&ccedil;&atilde;o do &eacute;xito das estrat&eacute;gias, estabelecidas as metas de curto e longo prazos e definidos os respectivos planos de a&ccedil;&atilde;o?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar de que forma os referenciais comparativos e os requisitos de partes interessadas s&atilde;o utilizados para a defini&ccedil;&atilde;o de metas.</li>\n</ul>\n','<ul>\n\t<li style=\"text-align:justify\">Apresentar os principais indicadores, suas metas e principais a&ccedil;&otilde;es planejadas ou projetos, relativos &atilde; implementa&ccedil;&atilde;o das estrat&eacute;gias.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">Os planos de a&ccedil;&atilde;o s&atilde;o tamb&eacute;m conhecidos como projetos estrat&eacute;gicos.</li>\n\t<li style=\"text-align: justify;\">Os resultados dos indicodores citados nesta quest&atilde;o devem ser apresentados no Crit&eacute;rio 8, exceto os relativos aos indicadores em inicio de uso. que n&atilde;o sejam compar&aacute;veis e n&atilde;o expressem requisitos de partes interessadas.</li>\n</ol>\n'),
  (970,173,'b','<p>Como as metas e os planos de a&ccedil;&atilde;o s&atilde;o desdobrados nas &aacute;reas respons&aacute;veis pelos processos da cadeia de valor?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar a forma de assegurar a coerência das metas e dos planos desdobrados com as estrat&eacute;gias, e tamb&eacute;m entre si, mantendo o alinhamento entre os indicadores utilizados na avalia&ccedil;&atilde;o do desempenho estrat&eacute;gico e aqueles utilizados na avalia&ccedil;&atilde;o do desempenho operacional;</li>\n\t<li style=\"text-align:justify\">Destacar de que forma os planos de a&ccedil;&atilde;o s&atilde;o desdobrados para os parceiros, quando aplic&aacute;vel.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align:justify\">Os processos da cadeia de vafor s&atilde;o aqueles citados no Perfil P1B(2).</li>\n\t<li style=\"text-align:justify\">Os n&iacute;veis de desempenho ou melhorias esperadas, que expressam os principais requisitos de partes interessadas, mencionados em 2.2a, utilizados ou n&atilde;o para estabelecer metas para os indicadores de avalia&ccedil;&atilde;o da implementa&ccedil;&atilde;o das estrat&eacute;gias, devem ser apresentados no Crit&eacute;rio 8, juntamente com os indicadores pertinentes.</li>\n\t<li style=\"text-align:justify\">O desdobramento das metas e planos de a&ccedil;&atilde;o deve abranger todas as &aacute;reas e n&iacute;veis da organiza&ccedil;&atilde;o.</li>\n</ol>\n'),
  (971,173,'c','<p>Como os recursos para a implementa&ccedil;&atilde;o dos planos de a&ccedil;&atilde;o s&atilde;o alocados?</p>\n','','','<ol>\n\t<li style=\"text-align: justify;\">A aloca&ccedil;&atilde;o de recursos visa assegurar a implementa&ccedil;&atilde;o dos planos de a&ccedil;&atilde;o que sustentam as estrat&eacute;gias.</li>\n\t<li style=\"text-align: justify;\">Os recursos para assegurar a implementa&ccedil;&atilde;o dos planos de a&ccedil;&atilde;o que sustentam as estrat&eacute;gias podem abranger os financeiros, humanos, materiais, tecnol&oacute;gicos, de capacidade produtiva ou de gest&atilde;o, informa&ccedil;&atilde;o, conhecimento e outros.</li>\n\t<li style=\"text-align: justify;\">A defini&ccedil;&atilde;o dos recursos financeiros e investimentos tamb&eacute;m &eacute; requerida em 7.3C.</li>\n</ol>\n'),
  (972,173,'d','<p>Como a implementa&ccedil;&atilde;o dos planos de a&ccedil;&atilde;o &eacute; acompanhada?</p>\n','<ul>\n\t<li style=\"text-align: justify;\">Destacar a maneira utilizada pela dire&ccedil;&atilde;o para acompanhar a situa&ccedil;&atilde;o da implementa&ccedil;&atilde;o dos planos de a&ccedil;&atilde;o de forma integrada.</li>\n</ul>\n','',''),
  (973,174,'a','<p>Como a organiza&ccedil;&atilde;o analisa e segmenta seu mercado atual e potencial, e define os clientes-alvo para seus produtos?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar os crit&eacute;rios adotados para segmenta&ccedil;&atilde;o do mercado e para defini&ccedil;&atilde;o de clientes-alvo.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">Os principais segmentos de mercado e dientes-aivo da organiza&ccedil;&atilde;o s&atilde;o requeridos no Perfil P1D(3).</li>\n</ol>\n'),
  (974,174,'b','<p>Como as necessidades, expectativas e comportamentos dos clientes atuais e dos clientes-alvo, e suas tendências, s&atilde;o identificados, analisados e traduzidos em requisitos de desempenho, e como s&atilde;o utilizados para defini&ccedil;&atilde;o e melhoria dos produtos e processos da organiza&ccedil;&atilde;o?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar os m&eacute;todos utilizados para definir a import&acirc;ncia relativa das necessidades, expectativas e comportamentos identificados;</li>\n\t<li style=\"text-align:justify\">Destacar de que forma a organiza&ccedil;&atilde;o se antecipa &agrave;s necessidades, expectativas e comportamentos de clientes atuais e potenciais;</li>\n\t<li style=\"text-align:justify\">Destacar de que forma a organiza&ccedil;&atilde;o atua em rede para ampliar o conhecimento sobre os clientes-alvo.</li>\n</ul>\n','<ul>\n\t<li style=\"text-align:justify\">Apresentar os principais requisitos e respectivos indicadores de desempenho relativos aos clientes e mercados.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">As principais necessidades e expectativas dos clientes s&atilde;o requeridas no Perfil P1C(1).</li>\n</ol>\n'),
  (975,174,'c','<p>Como as marcas s&atilde;o desenvolvidas no mercado?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar a forma de monitoramento, prote&ccedil;&atilde;o e divulga&ccedil;&atilde;o das marcas;</li>\n\t<li style=\"text-align:justify\">Destacar os m&eacute;todos utilizados para avaliar os n&iacute;veis de conhecimento dos clientes e mercados a respeito das marcas.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">O desenvolvimento das marcas tem o prop&oacute;sito de criar credibilidade, confian&ccedil;a e imagem positiva.</li>\n\t<li style=\"text-align: justify;\">As marcas podem incluir tanto as institucionais como as relacionadas a produtos.</li>\n</ol>\n'),
  (976,174,'d','<p>Como os produtos, incluindo os cuidados necess&aacute;rios ao seu uso e os riscos envolvidos, e tamb&eacute;m as a&ccedil;&otilde;es de melhoria da organiza&ccedil;&atilde;o s&atilde;o divulgados aos clientes e ao mercado?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar de que forma s&atilde;o asseguradas a clareza, a autenticidade e o conte&uacute;do adequado das mensagens divulgadas;</li>\n\t<li style=\"text-align:justify\">Destacar os m&eacute;todos utilizados para avaliar os n&iacute;veis de conhecimento dos clientes e mercados a respeito dos seus produtos e a&ccedil;&otilde;es.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">A divulga&ccedil;&atilde;o dos produtos e de outras informa&ccedil;&otilde;es importantes aos clientes e ao mercado visa assegurar que eles tenham pleno conhecimento da organiza&ccedil;&atilde;o, assim como criar credibilidade, confian&ccedil;a e imagem positiva.</li>\n</ol>\n'),
  (977,174,'e','<p>Como &eacute; avaliada a imagem da organiza&ccedil;&atilde;o perante os clientes e mercados?</p>\n','<p>Destacar a forma de defini&ccedil;&atilde;o dos atributos que se deseja evidenciar para caracterizar a imagem da organiza&ccedil;&atilde;o.</p>\n','<ul>\n\t<li style=\"text-align:justify\">Citar os atributos que se deseja evidenciar para caracterizar a imagem da organiza&ccedil;&atilde;o;</li>\n\t<li style=\"text-align:justify\">Apresentar as principais a&ccedil;&otilde;es implementadas para consolidar esses atributos</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">A imagem da organiza&ccedil;&atilde;o deve ser avaliada para verificar se os atributos que se deseja evidenciar est&atilde;o sendo percebidos pelos clientes e mercados-alvo.</li>\n\t<li style=\"text-align: justify;\">As principais a&ccedil;&otilde;es para consolidar os atributos que caracterizam a imagem podem ser implementadas pela pr&oacute;pria organiza&ccedil;&atilde;o ou por meio de parcerias.</li>\n</ol>\n'),
  (978,175,'a','<p>Como s&atilde;o estabelecidos, divulgados e monitorados os canais de intera&ccedil;&atilde;o com os clientes, considerando a segmenta&ccedil;&atilde;o do mercado e a defini&ccedil;&atilde;o dos clientes-alvo?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar os principais mecanismos ou canais de e monitorados os canais de intera&ccedil;&atilde;o com os clientes.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">Mencionar, quando aplic&aacute;vel, tamb&eacute;m os canais de relacionamento estabelecidos via parceiros, como distribuidores, revendedores, representantes e afins, para receber solicita&ccedil;&otilde;es, reclama&ccedil;&otilde;es e sugest&otilde;es.</li>\n</ol>\n'),
  (979,175,'b','<p>Como s&atilde;o tratadas as solicita&ccedil;&otilde;es, reclama&ccedil;&otilde;es ou sugest&otilde;es , formais ou informais, dos clientes?&nbsp;&nbsp;&nbsp;</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar a forma utilizada para comunicar aos clientes o resultado da an&aacute;lise dessas manifesta&ccedil;&otilde;es e as respectivas a&ccedil;&otilde;es implementadas, bem como para repassar essas informa&ccedil;&otilde;es a outras &aacute;reas da organiza&ccedil;&atilde;o e, quando aplic&aacute;vel, aos parceiros.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">O tratamento adequado das manifesta&ccedil;&otilde;es dos clientes visa assegurar que elas sejam pronta e eficazmente atendidas ou solucionadas.</li>\n\t<li style=\"text-align: justify;\">O monitoramento das reclama&ccedil;&otilde;es &eacute; importante para avaliar a insatisfa&ccedil;&atilde;o dos clientes.</li>\n</ol>\n'),
  (980,175,'c','<p>Como s&atilde;o acompanhadas as transa&ccedil;&otilde;es com os clientes, incluindo os novos, e a inser&ccedil;&atilde;o de novos produtos no mercado?</p>\n','','','<ol>\n\t<li style=\"text-align: justify;\">O acompanhamento das transa&ccedil;&otilde;es com os clientes atuais e clientes novos visa permitir &agrave; organiza&ccedil;&atilde;o gerar solu&ccedil;&otilde;es r&aacute;pidas e eficazes, evitar problemas de relacionamento e atender &agrave;s expectativas dos clientes. Com o mesmo objetivo, a organiza&ccedil;&atilde;o deve acompanhar a qualidade, desempenho, aceita&ccedil;&atilde;o, por exemplo, dos novos produtos inseridos no mercado e entregues aos clientes.</li>\n</ol>\n'),
  (981,175,'d','<p>Como s&atilde;o avaliadas a satisfa&ccedil;&atilde;o, a fidelidade e a insatisfa&ccedil;&atilde;o dos clientes?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar as formas utilizadas para avaliar a satisfa&ccedil;&atilde;o, a fidelidade e a insatisfa&ccedil;&atilde;o dos clientes em rela&ccedil;&atilde;o aos clientes dos concorrentes ou de organiza&ccedil;&otilde;es de referência.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">A avalia&ccedil;&atilde;o comparativa em rela&ccedil;&atilde;o aos clientes e organiza&ccedil;&otilde;es de referência deve ser realizada quando n&atilde;o h&aacute; concorrentes.</li>\n</ol>\n'),
  (982,175,'e','<p>Como as informa&ccedil;&otilde;es obtidas dos clientes s&atilde;o analisadas e utilizadas?</p>\n','','','<ol>\n\t<li style=\"text-align: justify;\">A utiliza&ccedil;&atilde;o eficaz das informa&ccedil;&otilde;es obtidas dos clientes possibilita intensificar a sua satisfa&ccedil;&atilde;o, torn&aacute;-los fi&eacute;is e incentiv&aacute;-los a recomendar os produtos da organiza&ccedil;&atilde;o. Al&eacute;m disso, as informa&ccedil;&otilde;es representam importantes subs&iacute;dios para desenvolver processos e produtos.</li>\n</ol>\n'),
  (983,175,'f','<p>Como s&atilde;o identificadas e desenvolvidas parcerias com clientes, distribuidores, representantes ou revendedores?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar as formas de desenvolvimento de redes de clientes ou usu&aacute;rios, distribuidores, representantes ou revendedores, bem como as formas de avalia&ccedil;&atilde;o do seu desempenho.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">As parcerias com clientes, distribuidores, representantes ou revendedores visam &atilde; manuten&ccedil;&atilde;o ou ao aumento da competitividade da organiza&ccedil;&atilde;o.</li>\n\t<li style=\"text-align: justify;\">Os distribuidores, representantes ou revendedores s&atilde;o requeridos no Perfil P1D(3), e as parcerias desenvolvidas s&atilde;o requeridas no Perfil P1D(6).</li>\n</ol>\n'),
  (984,176,'a','<p>Como as leis, regulamentos e normas ou c&oacute;digos de ades&atilde;o volunt&aacute;ria, aplic&aacute;veis &agrave; organiza&ccedil;&atilde;o s&atilde;o identificados, analisados e traduzidos em requisitos de desempenho?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar de que forma &eacute; assegurado o pleno atendimento aos requisitos relativos &agrave;s leis, regulamentos e normas ou c&oacute;digos de ades&atilde;o volunt&aacute;ria;</li>\n\t<li style=\"text-align:justify\">Destacar de que maneira s&atilde;o tratadas as pendências ou eventuais san&ccedil;&otilde;es relativas aos requisitos associados a leis e regulamentos aplic&aacute;veis &agrave; organiza&ccedil;&atilde;o. No caso de inexistência, declarar formalmente a situa&ccedil;&atilde;o.</li>\n</ul>\n','<ul>\n\t<li style=\"text-align:justify\">Apresentar os principais requisitos e respectivos indicadores de desempenho relativos &agrave; sociedade e relativos &agrave;s leis, regulamentos e normas ou c&oacute;digos de ades&atilde;o volunt&aacute;ria.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">As leis, regulamentos e normas ou c&oacute;digos de ades&atilde;o volunt&aacute;ria, aplic&aacute;veis &agrave; organiza&ccedil;&atilde;o, representam necessidades e expectativas da sociedade em geral em rela&ccedil;&atilde;o &agrave; organiza&ccedil;&atilde;o.</li>\n\t<li style=\"text-align: justify;\">As normas e c&oacute;digos de ades&atilde;o volunt&aacute;ria incluem aqueles pactuados por entes da sociedade e podem ser utilizados para o desenvolvimento dos padr&otilde;es gerenciais em 1.1 E. Podem abranger tanto os que possuem mecanismos de verifica&ccedil;&atilde;o independente ou por auto compromisso.</li>\n</ol>\n'),
  (985,176,'b','<p>Como a organiza&ccedil;&atilde;o identifica os aspectos e trata os impactos sociais e ambientais adversos decorrentes de seus produtos, processos e instala&ccedil;&otilde;es?</p>\n','','<ul>\n\t<li style=\"text-align:justify\">Apresentar as principais a&ccedil;&otilde;es e metas para eliminar, minimizar ou compensar os impactos sociais e ambientais negativos, incluindo o tratamento dos passivos sociais e ambientais.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">Os aspectos e impactos adversos s&atilde;o aqueles que a organiza&ccedil;&atilde;o tem influência e devem ser considerados ao longo do ciclo de vida do produto, desde o projeto at&eacute; a disposi&ccedil;&atilde;o final ou reutiliza&ccedil;&atilde;o.</li>\n\t<li style=\"text-align: justify;\">Entre os impactos sociais, incluem-se aqueles relativos a seguran&ccedil;a, sa&uacute;de e qualidade de vida dos usu&aacute;rios e da popula&ccedil;&atilde;o em geral, que possam advir dos produtos, processos e instala&ccedil;&otilde;es da organiza&ccedil;&atilde;o.</li>\n\t<li style=\"text-align: justify;\">Os principais impactos sociais e ambientais adversos, existentes e potenciais, bem como os passivos ambientais, se existirem, s&atilde;o requeridos no Perfil P1D(5).</li>\n</ol>\n'),
  (986,176,'c','<p>Como a organiza&ccedil;&atilde;o mant&eacute;m-se preparada para prevenir acidentes e responder &agrave;s eventuais situa&ccedil;&otilde;es de emergência?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar de que forma os acidentes, incidentes e ensaios de situa&ccedil;&otilde;es de emergência s&atilde;o analisados, investigados e documentados.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">A preven&ccedil;&atilde;o de acidentes deve abranger os relativos &agrave; sociedade e ao meio ambiente.</li>\n\t<li style=\"text-align: justify;\">A atua&ccedil;&atilde;o da organiza&ccedil;&atilde;o de forma preventiva e com prontid&atilde;o visa evitar ou mitigar os seus impactos adversos na sociedade e no meio ambiente, incluindo comunidades potencialmente impactadas.</li>\n</ol>\n'),
  (987,176,'d','<p>Como &eacute; propiciada a acessibilidade aos produtos, processos, informa&ccedil;&otilde;es e instala&ccedil;&otilde;es da organiza&ccedil;&atilde;o?</p>\n','','','<ol>\n\t<li style=\"text-align: justify;\">Acessibilidade aos processos significa possibilitar que pessoas com necessidades especiais possam exercer atividades produtivas na organiza&ccedil;&atilde;o</li>\n</ol>\n'),
  (988,176,'e','<p>Como a organiza&ccedil;&atilde;o seleciona e promove, de forma volunt&aacute;ria, a&ccedil;&otilde;es com vistas ao desenvolvimento sustent&aacute;vel?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar a forma de desenvolver parcerias ou de atuar em rede. conscientizar e envolver as partes interessadas na implementa&ccedil;&atilde;o ou apoio &agrave;s suas a&ccedil;&otilde;es e projetos ambientais.</li>\n</ul>\n','<ul>\n\t<li style=\"text-align:justify\">Citar as principais a&ccedil;&otilde;es implementadas como forma de contribui&ccedil;&atilde;o para a solu&ccedil;&atilde;o dos grandes temas mundiais e para a preserva&ccedil;&atilde;o ou recupera&ccedil;&atilde;o de ecossistemas, incluindo incorpora&ccedil;&atilde;o de tecnologias limpas ou de baixo impacto ambiental;</li>\n\t<li style=\"text-align:justify\">Citar as a&ccedil;&otilde;es efetuadas para a promo&ccedil;&atilde;o de educa&ccedil;&atilde;o ambiental &agrave; sua for&ccedil;a de trabalho, dientes, fornecedores e comunidade, bem como a&ccedil;&otilde;es para minimiza&ccedil;&atilde;o do consumo de recursos renov&aacute;veis, conserva&ccedil;&atilde;o de recursos n&atilde;o renov&aacute;veis na sua forma original, reciclagem e reutiliza&ccedil;&atilde;o de materiais.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">As parcerias desenvolvidas s&atilde;o requeridas no Perfil P1D(6).</li>\n\t<li style=\"text-align: justify;\">S&atilde;o exemplos de grandes temas mundiais: o aquecimento global, a redu&ccedil;&atilde;o da camada de oz&ocirc;nio, as mudan&ccedil;as clim&aacute;ticas, a destrui&ccedil;&atilde;o de florestas naturais, o respeito aos direitos humanos, o trabalho infantil ou degradante, a lavagem de dinheiro e tr&aacute;fico de pessoas, a pobreza extrema, a escassez de &aacute;gua pot&aacute;vel e temas afins.</li>\n</ol>\n'),
  (989,177,'a','<p>Como as necessidades e as expectativas de desenvolvimento da sociedade s&atilde;o identificadas, analisadas, traduzidas em requisitos de desempenho e utilizadas para a defini&ccedil;&atilde;o e a melhoria da atua&ccedil;&atilde;o social da organiza&ccedil;&atilde;o?</p>\n','','<ul>\n\t<li style=\"text-align:justify\">Apresentar os principais requisitos e respectivos indicadores de desempenho relativos ao desenvolvimento da sociedade.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">As principais necessidades e expectativas de desenvolvimento da sociedade relativas a essa quest&atilde;o, al&eacute;m do cumprimento das leis e regulamentos tratados em 4.1a, s&atilde;o requeridas no Perfil P1C(1).</li>\n</ol>\n'),
  (990,177,'b','<p>Como a organiza&ccedil;&atilde;o direciona esfor&ccedil;os para o desenvolvimento da sociedade?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar os crit&eacute;rios estabelecidos para a sele&ccedil;&atilde;o, acompanhamento e avalia&ccedil;&atilde;o do &eacute;xito destes projetos sociais;</li>\n\t<li style=\"text-align:justify\">Destacar a forma de desenvolver parcerias ou de atuar em rede e envolver partes interessadas na implementa&ccedil;&atilde;o de projetos sociais.</li>\n</ul>\n','<ul>\n\t<li style=\"text-align:justify\">Citar os principais projetos implementados ou apoiados, incluindo aqueles voltados ao desenvolvimento da competitividade e sustentabilidade das organiza&ccedil;&otilde;es do setor, do local, da regi&atilde;o ou do pa&iacute;s.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">Os esfor&ccedil;os para o fortalecimento da sociedade incluem a execu&ccedil;&atilde;o ou apoio a projetos voltados ao desenvolvimento nacional, regional, local ou setorial, em fun&ccedil;&atilde;o do porte e tipo de atividade da organiza&ccedil;&atilde;o.</li>\n\t<li style=\"text-align: justify;\">As ofertas de vagas para estagi&aacute;rios podem ser consideradas a&ccedil;&otilde;es de desenvolvimento social.</li>\n\t<li style=\"text-align: justify;\">As parcerias desenvolvidas s&atilde;o requeridas no Perfil P1D(6).</li>\n</ol>\n'),
  (991,177,'c','<p>Como &eacute; avaliado o grau de satisfa&ccedil;&atilde;o da sociedade em rela&ccedil;&atilde;o aos projetos sociais da organiza&ccedil;&atilde;o?</p>\n','','',''),
  (992,177,'d','<p>Como as informa&ccedil;&otilde;es obtidas da sociedade s&atilde;o analisadas e utilizadas para melhorar a atua&ccedil;&atilde;o social?</p>\n','','','<ol>\n\t<li style=\"text-align: justify;\">O uso eficaz das informa&ccedil;&otilde;es obtidas possibilita a intensifica&ccedil;&atilde;o da satisfa&ccedil;&atilde;o da sociedade, bem como o aperfei&ccedil;oamento e o desenvolvimento dos projetos sociais atuais ou futuros.</li>\n</ol>\n'),
  (993,177,'e','<p>Como a organiza&ccedil;&atilde;o avalia e zela por sua imagem perante a sociedade?</p>\n','','',''),
  (994,178,'a','<p>Como s&atilde;o identificadas as necessidades de informa&ccedil;&otilde;es, e de seu tratamento, para operar e gerenciar a organiza&ccedil;&atilde;o?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar de que forma a organiza&ccedil;&atilde;o responde prontamente &agrave;s novas demandas por informa&ccedil;&otilde;es estrat&eacute;gicas e operacionais.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">As necessidades de informa&ccedil;&otilde;es para operar a organiza&ccedil;&atilde;o abrangem aquelas que apoiam as opera&ccedil;&otilde;es dos processos da cadeia de valor.</li>\n\t<li style=\"text-align: justify;\">As necessidades de informa&ccedil;&otilde;es para gerenciar a organiza&ccedil;&atilde;o abrangem indicadores de desempenho, situa&ccedil;&atilde;o de planos, projetos e or&ccedil;amentos, relat&oacute;rios gerenciais, resultados de pesquisas e outras vari&aacute;veis importantes do ambiente, necess&aacute;rias para apoiar o processo decis&oacute;rio em todos os n&iacute;veis e &aacute;reas da organiza&ccedil;&atilde;o.</li>\n\t<li style=\"text-align: justify;\">O tratamento de informa&ccedil;&otilde;es diz respeito ao processamento necess&aacute;rio para as informa&ccedil;&otilde;es requeridas se apresentarem &iacute;ntegras e completas, desde a coleta dos dados de origem.</li>\n</ol>\n'),
  (995,178,'b','<p>Como s&atilde;o definidos, desenvolvidos, implantados e melhorados os principais sistemas de informa&ccedil;&atilde;o, considerando as necessidades identificadas?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar de que forma &eacute; assegurada a atualiza&ccedil;&atilde;o tecnol&oacute;gica dos sistemas de informa&ccedil;&atilde;o;</li>\n\t<li style=\"text-align:justify\">Destacar a maneira de promover a integra&ccedil;&atilde;o da organiza&ccedil;&atilde;o com clientes, fornecedores e outras partes interessadas externas, por meio de sistemas de informa&ccedil;&atilde;o.</li>\n</ul>\n','<ul>\n\t<li style=\"text-align:justify\">Citar os principais sistemas de informa&ccedil;&atilde;o em uso e sua finalidade;</li>\n\t<li style=\"text-align:justify\">Citar as principais solu&ccedil;&otilde;es adotadas, visando &agrave; integra&ccedil;&atilde;o das informa&ccedil;&otilde;es, dos sistemas e partes interessadas externas.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">Os sistemas de informa&ccedil;&atilde;o podem incluir sistemas n&atilde;o informatizados, visando atender &agrave;s necessidades de informa&ccedil;&atilde;o e de seu tratamento, como. por exemplo, sistemas de gest&atilde;o &agrave; vista, sistemas de controle visuais, sistema de manuais,sistema de prontu&aacute;rios, sistemas de documentos para opera&ccedil;&atilde;o manual.</li>\n</ol>\n'),
  (996,178,'c','<p>Como a infra estrutura para a disponibiliza&ccedil;&atilde;o das informa&ccedil;&otilde;es aos usu&aacute;rios &eacute; compatibilizada com o crescimento do neg&oacute;cio e da demanda por informa&ccedil;&otilde;es?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar os m&eacute;todos utilizados para assegurar o acesso r&aacute;pido e f&aacute;cil &agrave;s informa&ccedil;&otilde;es pelos usu&aacute;rios;</li>\n\t<li style=\"text-align:justify\">Destacar as formas utilizadas para assegurar a continuidade da infra estrutura, para a disponibiliza&ccedil;&atilde;o de informa&ccedil;&otilde;es em situa&ccedil;&otilde;es de emergência;</li>\n\t<li style=\"text-align:justify\">Destacar de que forma &eacute; avaliada a satisfa&ccedil;&atilde;o dos usu&aacute;rios dos servi&ccedil;os de informa&ccedil;&atilde;o e de comunica&ccedil;&atilde;o, informatizados ou n&atilde;o.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">A infra estrutura para a disponibiliza&ccedil;&atilde;o das informa&ccedil;&otilde;es aos usu&aacute;rios inclui a de comunica&ccedil;&atilde;o.</li>\n\t<li style=\"text-align: justify;\">Os usu&aacute;rios podem abranger as pessoas da for&ccedil;a de trabalho e integrantes de outras partes interessadas.</li>\n\t<li style=\"text-align: justify;\">As situa&ccedil;&otilde;es de emergência s&atilde;o aquelas que podem causar a interrup&ccedil;&atilde;o dos servi&ccedil;os de informa&ccedil;&atilde;o.</li>\n</ol>\n'),
  (997,178,'d','<p>Como a seguran&ccedil;a das informa&ccedil;&otilde;es &eacute; mantida?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar os m&eacute;todos utilizados para assegurar a atualiza&ccedil;&atilde;o, confidencialidade e integridade das informa&ccedil;&otilde;es.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">A confidencialidade pressup&otilde;e que somente pessoas autorizadas tenham acesso &agrave;s informa&ccedil;&otilde;es.</li>\n\t<li style=\"text-align: justify;\">A integridade trata da salvaguarda da exatid&atilde;o e completeza da informa&ccedil;&atilde;o recebida, armazenada e distribu&iacute;da, bem como dos m&eacute;todos de processamento.</li>\n</ol>\n'),
  (998,179,'a','<p>Como a organiza&ccedil;&atilde;o identifica os conhecimentos mais importantes para realizar sua miss&atilde;o e implementar as estrat&eacute;gias?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar as formas utilizadas para classificar os tipos de conhecimento e os crit&eacute;rios para definir os que s&atilde;o mais importantes.</li>\n</ul>\n','<ul>\n\t<li style=\"text-align:justify\">Apresentar as principais fontes de conhecimento utilizadas, internas e externas;</li>\n\t<li style=\"text-align:justify\">Citar os tipos de conhecimentos mais importantes para a organiza&ccedil;&atilde;o.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">Os conhecimentos da organiza&ccedil;&atilde;o para realizar sua miss&atilde;o podem incluir conhecimentos relativos a clientes e mercados, produtos, processos, tecnologias, pessoas, fornecedores, relacionamentos, articula&ccedil;&otilde;es e mem&oacute;ria organizacional.</li>\n\t<li style=\"text-align: justify;\">A classifica&ccedil;&atilde;o dos conhecimentos &eacute; importante para definir os seus tipos e prop&oacute;sitos, e fundamental para identificar os conhecimentos mais importantes.</li>\n\t<li style=\"text-align: justify;\">Os crit&eacute;rios de classifica&ccedil;&atilde;o podem incluir pertinência, valor, confiabilidade, prop&oacute;sito, redund&acirc;ncia, grau de incerteza ou outros par&acirc;metros.</li>\n</ol>\n'),
  (999,179,'b','<p>Como a organiza&ccedil;&atilde;o desenvolve os conhecimentos mais importantes?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar de que maneira a organiza&ccedil;&atilde;o cria um ambiente favor&aacute;vel &agrave; busca e gera&ccedil;&atilde;o de conhecimento;</li>\n\t<li style=\"text-align:justify\">Destacar a forma de utiliza&ccedil;&atilde;o de redes de pessoas ou organiza&ccedil;&otilde;es para apoiar a busca e o desenvolvimento do conhecimento.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">A busca do conhecimento abrange a pesquisa e apropria&ccedil;&atilde;o de conhecimento externo &agrave; organiza&ccedil;&atilde;o.</li>\n\t<li style=\"text-align: justify;\">O desenvolvimento do conhecimento pode incluir, al&eacute;m do treinamento, o estabelecimento de equipes de conhecimento, de melhoria de processos ou de aprendizado, centros de competência, pesquisa e desenvolvimento, comunidades de pr&aacute;tica, registro de li&ccedil;&otilde;es aprendidas, fomento &agrave; inova&ccedil;&atilde;o e busca de melhores pr&aacute;ticas.</li>\n</ol>\n'),
  (1000,179,'c','<p>Como os conhecimentos mais importantes s&atilde;o retidos e protegidos?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar as formas utilizadas para armazenar os conhecimentos;</li>\n\t<li style=\"text-align:justify\">Destacar os m&eacute;todos utilizados para atrair e reter pessoas e parceiros considerados chave e detentores dos conhecimentos mais importantes da organiza&ccedil;&atilde;o.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">A reten&ccedil;&atilde;o, necess&aacute;ria para permitir a difus&atilde;o e a utiliza&ccedil;&atilde;o do conhecimento, pressup&otilde;e a sua documenta&ccedil;&atilde;o e convers&atilde;o do conhecimento t&aacute;cito em conhecimento expl&iacute;cito.</li>\n\t<li style=\"text-align: justify;\">A prote&ccedil;&atilde;o do conhecimento visa assegurar que o mesmo seja mantido na organiza&ccedil;&atilde;o e que as vantagens competitivas.</li>\n</ol>\n'),
  (1001,179,'d','<p>Como os conhecimentos da organiza&ccedil;&atilde;o s&atilde;o difundidos e utilizados?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar as formas utilizadas para que os conhecimentos retidos possam ser r&aacute;pida e facilmente acessados e utilizados, onde pertinentes;</li>\n\t<li style=\"text-align:justify\">Destacar as formas de compartilhamento do conhecimento, interna e externamente, quando pertinente;</li>\n\t<li style=\"text-align:justify\">Destacar a forma de utiliza&ccedil;&atilde;o de redes de pessoas ou organiza&ccedil;&otilde;es para apoiar a difus&atilde;o do conhecimento.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">O conhecimento deve ser difundido &agrave;s pessoas da for&ccedil;a de trabalho e demais partes interessadas que tenham a necessidade expl&iacute;cita de utiliz&aacute;-lo.</li>\n</ol>\n'),
  (1002,180,'a','<p>Como a organiza&ccedil;&atilde;o do trabalho &eacute; definida?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar de que forma a organiza&ccedil;&atilde;o do trabalho estimula a resposta r&aacute;pida e o aprendizado organizacional;</li>\n\t<li style=\"text-align:justify\">Destacar de que forma a organiza&ccedil;&atilde;o do trabalho facilita a atua&ccedil;&atilde;o dos respons&aacute;veis pelos processos na estrutura organizacional adotada;</li>\n\t<li style=\"text-align:justify\">Destacar a autonomia dos diversos n&iacute;veis da for&ccedil;a de trabalho para definir, gerir e melhorar os processos da organiza&ccedil;&atilde;o e para buscar inova&ccedil;&otilde;es;</li>\n\t<li style=\"text-align:justify\">Destacar a forma utilizada para planejar o quadro de pessoal e suas responsabilidades, e o crit&eacute;rio utilizado para definir as atividades terceirizadas, quando existirem.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align:justify\">A organiza&ccedil;&atilde;o do trabalho em &aacute;reas, grupos ou estruturas afins deve ser concebida em alinhamento com as competências requeridas pelo modelo de neg&oacute;cio, estrat&eacute;gias e processos, visando ao alto desempenho e a inova&ccedil;&atilde;o;</li>\n\t<li style=\"text-align:justify\">As caracter&iacute;sticas da organizo&ccedil;&atilde;o do trabalho, para estimular a resposta r&aacute;pida e o aprendizado, devem incluir a comunica&ccedil;&atilde;o eficaz entre as pessoas e entre as equipes, inclusive de diferentes localidades e &aacute;reas, bem como a forma&ccedil;&atilde;o de redes de coopera&ccedil;&atilde;o.</li>\n</ol>\n'),
  (1003,180,'b','<p>Como as competências necess&aacute;rias para o exerc&iacute;cio pleno das fun&ccedil;&otilde;es pelas pessoas, incluindo l&iacute;deres, s&atilde;o identificadas?</p>\n','','<ul>\n\t<li style=\"text-align:justify\">Apresentar as principais competências identificadas para as pessoas em geral e especificamente para os lideres.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">A identifica&ccedil;&atilde;o das competências &eacute; fundamental para a defini&ccedil;&atilde;o dos pap&eacute;is de cada pessoa na organiza&ccedil;&atilde;o, da estrutura de cargos e da organiza&ccedil;&atilde;o do trabalho.</li>\n\t<li style=\"text-align: justify;\">As principais competências identificadas a serem apresentadas podem variar em fun&ccedil;&atilde;o dos diferentes grupos de pessoas da for&ccedil;a de trabalho.</li>\n</ol>\n'),
  (1004,180,'c','<p>Como as pessoas s&atilde;o selecionadas e contratadas, interna e externamente?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar de que maneira &eacute; acompanhada, no mercado de trabalho, a disponibilidade de pessoas com as competências requeridas pela organiza&ccedil;&atilde;o e de que forma &eacute; tratada a eventual escassez;</li>\n\t<li style=\"text-align:justify\">Destacar de que forma a organiza&ccedil;&atilde;o estimula a inclus&atilde;o de minorias e assegura a n&atilde;o discrimina&ccedil;&atilde;o e a igualdade de oportunidades para todas as pessoas.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">O acompanhamento da disponibilidade de pessoas com competências requeridas pela organiza&ccedil;&atilde;o no mercado de trabalho tem por objetivo detectar eventual escassez e tendências que possam comprometer o êxito das estrat&eacute;gias.</li>\n</ol>\n'),
  (1005,180,'d','<p>Como as pessoas rec&eacute;m-contratadas s&atilde;o preparadas para o exerc&iacute;cio de suas fun&ccedil;&otilde;es e integradas &agrave; cultura organizacional?</p>\n','','',''),
  (1006,180,'e','<p>Como o desempenho das pessoas, incluindo lideres, e das equipes &eacute; avaliado e como as necessidades de melhoria s&atilde;o acordadas com as pessoas?</p>\n','<ul>\n\t<li style=\"text-align: justify;\">Destacar de que forma a avalia&ccedil;&atilde;o estimula a busca do alto desempenho, o alcance de metas, a cultura da excelência e o desenvolvimento profissional das pessoas e equipes.</li>\n</ul>\n','',''),
  (1007,180,'f','<p>Como a remunera&ccedil;&atilde;o, o reconhecimento e os incentivos estimulam a busca do alto desempenho, o alcance de metas e a cultura de excelência?</p>\n','','','<ol>\n\t<li style=\"text-align:justify\">O termo &quot;reconhecimento&quot; abrange a identifica&ccedil;&atilde;o e o refor&ccedil;o de comportamentos de alto desempenho das pessoas.</li>\n\t<li style=\"text-align:justify\">O termo &quot;incentivo&quot; abrange o refor&ccedil;o desses comportamentos, condicionado a n&iacute;veis de desempenho pr&eacute;-determinados.</li>\n\t<li style=\"text-align:justify\">A identifica&ccedil;&atilde;o de oportunidades de reconhecimento pode incluir a percep&ccedil;&atilde;o de realiza&ccedil;&otilde;es ou contribui&ccedil;&otilde;es extraordin&aacute;rias das pessoas, o desempenho excepcional ou outros fatores pass&iacute;veis de reconhecimento.</li>\n\t<li style=\"text-align:justify\">As condicionantes do incentivo podem abranger alcance de metas, aquisi&ccedil;&atilde;o de conhecimentos, habilidades e competências ou outros fatores indutores do alto desempenho. O refor&ccedil;o de comportamentos pode incluir premia&ccedil;&otilde;es, recompensas ou oportunidades especiais, formais ou informais, individuais ou em grupo.</li>\n</ol>\n'),
  (1008,181,'a','<p>Como s&atilde;o identificadas as necessidades de capacita&ccedil;&atilde;o e desenvolvimento das pessoas?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar as formas de participa&ccedil;&atilde;o dos l&iacute;deres e das pr&oacute;prias pessoas a serem capacitadas e desenvolvidas, na identifica&ccedil;&atilde;o das necessidades.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">A identifica&ccedil;&atilde;o das necessidades de capacita&ccedil;&atilde;o e desenvolvimento das pessoas deve abranger o êxito de estrat&eacute;gias, a forma&ccedil;&atilde;o da cultura da excelência e a melhoria do desempenho individual.</li>\n</ol>\n'),
  (1009,181,'b','<p>Como &eacute; concebida a forma de realiza&ccedil;&atilde;o dos programas de capacita&ccedil;&atilde;o e de desenvolvimento?</p>\n','<ul>\n\t<li style=\"text-align: justify;\">Destacar de que forma a concep&ccedil;&atilde;o dos programas de capacita&ccedil;&atilde;o e desenvolvimento levam em considera&ccedil;&atilde;o as necessidades da organiza&ccedil;&atilde;o e das pessoas.</li>\n</ul>\n','<ul>\n\t<li style=\"text-align: justify;\">Citar os principais programas, o p&uacute;blico-alvo, destacando aqueles relacionados ao desenvolvimento de l&iacute;deres e &agrave; forma&ccedil;&atilde;o da cultura da excelência.</li>\n</ul>\n',''),
  (1010,181,'c','<p>Como s&atilde;o identificados e preparados os novos l&iacute;deres e os sucessores para os l&iacute;deres atuais?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar o papel da dire&ccedil;&atilde;o no desenvolvimento de l&iacute;deres da organiza&ccedil;&atilde;o.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">A identifica&ccedil;&atilde;o e a prepara&ccedil;&atilde;o de sucessores para os l&iacute;deres atuais devem abranger todas as fun&ccedil;&otilde;es de lideran&ccedil;a.</li>\n</ol>\n'),
  (1011,181,'d','<p>Como a efic&aacute;cia dos programas de capacita&ccedil;&atilde;o e desenvolvimento &eacute; avaliada?</p>\n','','','<ol>\n\t<li style=\"text-align: justify;\">A efic&aacute;cia dos programas de capacita&ccedil;&atilde;o deve ser avaliada considerando o alcance dos objetivos estrat&eacute;gicos e operacionais da organiza&ccedil;&atilde;o.</li>\n</ol>\n'),
  (1012,181,'e','<p>Como a organiza&ccedil;&atilde;o promove o desenvolvimento integral das pessoas como indiv&iacute;duos, cidad&atilde;os e profissionais?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar os m&eacute;todos de orienta&ccedil;&atilde;o ou aconselhamento, empregabilidade e desenvolvimento de carreira.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">O desenvolvimento das pessoas como indiv&iacute;duos aborda temas relacionados &agrave; melhoria dos comportamentos e atitudes psicossociais que favorecem o trabalho em equipe e o relacionamento interpessoal. Por exemplo: programas de treinamento comportamental, comunica&ccedil;&atilde;o interpessoal, vivência em grupo, constru&ccedil;&atilde;o de times, trabalho em equipe, servi&ccedil;os de assistência psicol&oacute;gica e social, entre outros.</li>\n\t<li style=\"text-align: justify;\">O desenvolvimento das pessoas como cidad&atilde;os aborda aspectos voltados ao exerc&iacute;cio da &eacute;tica e cidadania na comunidade. Por exemplo: programas de orienta&ccedil;&atilde;o ou assistência jur&iacute;dica, treinamento em direitos do cidad&atilde;o, funcionamento e canais da administra&ccedil;&atilde;o p&uacute;blica e da justi&ccedil;a, cr&eacute;dito respons&aacute;vel, est&iacute;mulo ou apoio ao voluntariado e &eacute;tica empresarial, entre outros.</li>\n\t<li style=\"text-align: justify;\">O desenvolvimento das pessoas como profissionais aborda temas que aumentem a competência, por meio da qualifica&ccedil;&atilde;o em novas habilidades que as tornem mais preparadas profissionalmente, independentemente das fun&ccedil;&otilde;es que exercem. Por exemplo: programas de uso e atualiza&ccedil;&atilde;o em inform&aacute;tica, capacita&ccedil;&atilde;o em l&iacute;ngua estrangeira, comunica&ccedil;&atilde;o oral e escrita, uso racional de correio eletr&ocirc;nico, m&eacute;todos de an&aacute;lise e solu&ccedil;&atilde;o de problemas, apresenta&ccedil;&otilde;es eficazes, uso eficiente do tempo e trabalho administrativo completo, entre outros.</li>\n</ol>\n'),
  (1013,182,'a','<p>Como s&atilde;o identificados os perigos e tratados os riscos relacionados &agrave; sa&uacute;de ocupacional e &agrave; seguran&ccedil;a?</p>\n','','<ul>\n\t<li style=\"text-align:justify\">Citar os principais programas e as metas para eliminar ou minimizar esses riscos.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">A sa&uacute;de ocupacional abrange aspectos relativos &agrave; ergonomia.</li>\n</ol>\n'),
  (1014,182,'b','<p>Como as necessidades e expectativas das pessoas da for&ccedil;a de trabalho e do mercado de trabalho s&atilde;o identificadas, analisadas, traduzidas em requisitos de desempenho e utilizadas para o desenvolvimento de pol&iacute;ticas e programas de pessoal e de benef&iacute;cios a elas oferecidos?</p>\n','','<ul>\n\t<li style=\"text-align:justify\">Apresentar, de forma comparativa com o mercado de trabalho, os principais benef&iacute;cios oferecidos &agrave;s pessoas na organiza&ccedil;&atilde;o;</li>\n\t<li style=\"text-align:justify\">Apresentar os principais requisitos e respectivos indicadores de desempenho relativos &agrave;s pessoas da for&ccedil;a de trabalho.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">A identifica&ccedil;&atilde;o das necessidades e expectativas do mercado de trabalho tem por objetivo o desenvolvimento de pol&iacute;ticas e programas de pessoal e de benef&iacute;cios que sejam competitivos.</li>\n\t<li style=\"text-align: justify;\">As principais necessidades e expectativas das pessoas da for&ccedil;a de trabalho s&atilde;o requeridas no Perfil P1C(1).</li>\n\t<li style=\"text-align: justify;\">Os benef&iacute;cios oferecidos &agrave;s pessoas incluem quaisquer servi&ccedil;os, facilidades, ambiente de trabalho e outros atrativos valorizados pelas pessoas na manuten&ccedil;&atilde;o da rela&ccedil;&atilde;o de trabalho com a organiza&ccedil;&atilde;o.</li>\n</ol>\n'),
  (1015,182,'c','<p>Como o bem-estar, a satisfa&ccedil;&atilde;o e o comprometimento das pessoas s&atilde;o avaliados e desenvolvidos?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar de que forma s&atilde;o tratados, com a participa&ccedil;&atilde;o de l&iacute;deres dos diversos n&iacute;veis, os fatores identificados na avalia&ccedil;&atilde;o que afetam o bem-estar, a satisfa&ccedil;&atilde;o e o comprometimento das pessoas.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">Os fatores que afetam o bem-estar s&atilde;o aqueles relativos &agrave; promo&ccedil;&atilde;o de um ambiente de trabalho agrad&aacute;vel, tranquilo e equilibrado, no qual as pessoas se sintam bem.</li>\n\t<li style=\"text-align: justify;\">Os fatores que afetam a satisfa&ccedil;&atilde;o s&atilde;o aqueles relativos ao atendimento de necessidades das pessoas e que determinam sua preferência em trabalhar para a organiza&ccedil;&atilde;o.</li>\n\t<li style=\"text-align: justify;\">Os fatores que afetam o comprometimento s&atilde;o aqueles relativos ao compromisso assumido pelas pessoas com suas responsabilidades profissionais e com a causa da organiza&ccedil;&atilde;o.</li>\n</ol>\n'),
  (1016,182,'d','<p>Como &eacute; mantido um clima organizacional favor&aacute;vel &agrave; criatividade, a inova&ccedil;&atilde;o, &atilde; excelência no desempenho e ao desenvolvimento profissional das pessoas e das equipes?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar as formas utilizadas para mobilizar as pessoas em torno da explora&ccedil;&atilde;o de ideias criativas, buscando inova&ccedil;&otilde;es.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">Podem ser citadas caracter&iacute;sticas especiais das instala&ccedil;&otilde;es e do ambiente, ferramentas de trabalho, servi&ccedil;os e outros aspectos voltados &agrave; promo&ccedil;&atilde;o do bem-estar, satisfa&ccedil;&atilde;o e comprometimento.</li>\n</ol>\n'),
  (1017,182,'e','<p>Como a organiza&ccedil;&atilde;o promove a melhoria da qualidade de vida das pessoas fora do ambiente de trabalho?</p>\n','','<ul>\n\t<li style=\"text-align: justify;\">Citar as principais a&ccedil;&otilde;es desenvolvidas.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">A promo&ccedil;&atilde;o da qualidade de vida fora do ambiente de trabalho aborda temas relacionados &agrave; sa&uacute;de f&iacute;sica e mental das pessoas, e aos servi&ccedil;os de apoio operacional que facilitam o seu dia a dia. Por exemplo: programas de bons h&aacute;bitos alimentares, promo&ccedil;&atilde;o da sa&uacute;de total, seguran&ccedil;a dom&eacute;stica e manuten&ccedil;&atilde;o dom&eacute;stica, condu&ccedil;&atilde;o segura, seguran&ccedil;a p&uacute;blica, primeiros socorros, economia dom&eacute;stica e servi&ccedil;os de orienta&ccedil;&atilde;o desportiva, grêmio ou clube familiar, creche, transporte, acesso &agrave; internet e uso da telefonia, entre outros.</li>\n</ol>\n'),
  (1018,183,'a','<p>Como s&atilde;o definidos os requisitos aplic&aacute;veis aos produtos e aos processos da cadeia de valor?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar os m&eacute;todos utilizados para traduzir os requisitos dos clientes e de outras partes interessadas em requisitos para os processos e produtos.</li>\n</ul>\n','<ul>\n\t<li style=\"text-align:justify\">Apresentar os principais requisitos relativos aos produtos e processos da cadeia de valor a serem atendidos e os respectivos indicadores de desempenho.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">Os requisitos dos produtos e processos devem levar em considera&ccedil;&atilde;o os requisitos dos clientes, da sociedade e de outras partes interessadas aplic&aacute;veis.</li>\n</ol>\n'),
  (1019,183,'b','<p>Como s&atilde;o desenvolvidos os novos produtos?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar as metodologias de projeto utilizadas para desenvolvimento de novos produtos.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">A express&atilde;o &quot;novos produtos&quot; inclui varia&ccedil;&otilde;es em produtos existentes.</li>\n\t<li style=\"text-align: justify;\">Os novos produtos devem ser desenvolvidos para atender aos requisitos dos clientes e de outras partes interessadas.</li>\n</ol>\n'),
  (1020,183,'c','<p>Como s&atilde;o projetados os processos da cadeia de valor?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar os m&eacute;todos adotados para assegurar que o projeto dos processos inclua a defini&ccedil;&atilde;o dos pap&eacute;is, responsabilidades e autoridades dos respons&aacute;veis pelos processos;</li>\n\t<li style=\"text-align:justify\">Destacar as metodologias utilizadas nos projetos, considerando-se os diferentes tipos de processos.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">Os processos da cadeia de valor devem ser projetados, concebidos ou planejados para atender aos requisitos dos clientes e de outras partes interessadas, e, quando aplic&aacute;vel, de outros processos da cadeia de valor deles dependentes.</li>\n\t<li style=\"text-align: justify;\">A utiliza&ccedil;&atilde;o de metodologias, como as de gest&atilde;o de processos, viabiliza o projeto dos processos de forma estruturada, definindo seus respons&aacute;veis, *donos&#39; ou lideres, estabelecendo os objetivos e requisitos de desempenho dos processos de forma transparente, realizando o desdobramento ou mapeamento das atividades que comp&otilde;em cada processo, a an&aacute;lise de pontos cr&iacute;ticos de controle, o estabelecimento dos indicadores mais importantes e suas respectivas metas, al&eacute;m da distribui&ccedil;&atilde;o das atividades entre as equipes e outros fatores.</li>\n</ol>\n'),
  (1021,183,'d','<p>Como &eacute; avaliado o potencial de ideias criativas a serem convertidas em inova&ccedil;&otilde;es sustent&aacute;veis nos produtos e processos?</p>\n','','','<ol>\n\t<li style=\"text-align: justify;\">A avalia&ccedil;&atilde;o do potencial de ideias criativas pode incluir a realiza&ccedil;&atilde;o de experimentos por meio de pilotos, prot&oacute;tipos, modelagem e simula&ccedil;&otilde;es.</li>\n\t<li style=\"text-align: justify;\">As inova&ccedil;&otilde;es sustent&aacute;veis s&atilde;o aquelas concebidas considerando tamb&eacute;m seus potenciais impactos socioambientais.</li>\n</ol>\n'),
  (1022,183,'e','<p>Como &eacute; assegurado o atendimento dos requisitos aplic&aacute;veis aos processos da cadeia de valor?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar os m&eacute;todos de padroniza&ccedil;&atilde;o e controle, incluindo os de autogerenciamento das opera&ccedil;&otilde;es;</li>\n\t<li style=\"text-align:justify\">Destacar a forma de tratamento de eventuais n&atilde;o conformidades identificadas e de implementa&ccedil;&atilde;o das a&ccedil;&otilde;es corretivas;</li>\n\t<li style=\"text-align:justify\">Destacar de que maneira os sistemas de informa&ccedil;&atilde;o propiciam agilidade e integra&ccedil;&atilde;o na opera&ccedil;&atilde;o e no autogerenciamento dos processos da cadeia de valor.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">Os m&eacute;todos de autogerenciamento referem-se &agrave;s atividades de gerenciamento realizadas pelos pr&oacute;prios executantes das opera&ccedil;&otilde;es, visando ao cumprimento dos padr&otilde;es estabelecidos.</li>\n</ol>\n'),
  (1023,183,'f','<p>Como a organiza&ccedil;&atilde;o analisa e melhora os produtos e os processos da cadeia de valor?</p>\n','<ul>\n\t<li style=\"text-align: justify;\">Destacar os m&eacute;todos utilizados para reduzir a variabilidade e aumentar a confiabilidade e a ecoeficiência.</li>\n</ul>\n','<ul>\n\t<li style=\"text-align: justify;\">Citar as principais melhorias recentes implementadas em produtos e em processos da cadeia de valor.</li>\n</ul>\n',''),
  (1024,183,'g','<p>Como a organiza&ccedil;&atilde;o avalia, incorpora e implanta as novas tecnologias no desenvolvimento e na melhoria dos produtos e processos da cadeia de valor?</p>\n','','','<ol>\n\t<li style=\"text-align: justify;\">A gest&atilde;o de novas tecnologias deve ser considerada para que a organiza&ccedil;&atilde;o antecipe tendências e esteja preparada para os desafios futuros.</li>\n</ol>\n'),
  (1025,183,'h','<p>Como s&atilde;o investigadas as caracter&iacute;sticas de produtos e de processos da cadeia de valor, de concorrentes ou de outras organiza&ccedil;&otilde;es de referência, para buscar melhorias?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar os crit&eacute;rios de sele&ccedil;&atilde;o das organiza&ccedil;&otilde;es de referência e as formas de obten&ccedil;&atilde;o e utiliza&ccedil;&atilde;o dessas informa&ccedil;&otilde;es.</li>\n</ul>\n','<ul>\n\t<li style=\"text-align:justify\">Citar as principais&nbsp; em melhorias recentemente implementadas em produtos e em processos&nbsp; da cadeia de valor em decorrência dessas compara&ccedil;&otilde;es.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">Os produtos e processos que s&atilde;o objeto da investiga&ccedil;&atilde;o referem-se &agrave;queles com finalidade similar ao da organiza&ccedil;&atilde;o.</li>\n\t<li style=\"text-align: justify;\">As organiza&ccedil;&otilde;es de referência incluem aquelas de dentro e de fora do ramo ou setor de atua&ccedil;&atilde;o da organiza&ccedil;&atilde;o.</li>\n</ol>\n'),
  (1026,184,'a','<p>Como a organiza&ccedil;&atilde;o desenvolve a sua cadeia de suprimentos imediata e nela identifica potenciais fornecedores e parceiros?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar as formas de est&iacute;mulo ao desenvolvimento dos fornecedores locais;</li>\n\t<li style=\"text-align:justify\">Destacar a atua&ccedil;&atilde;o da organiza&ccedil;&atilde;o para erradicar o trabalho infantil, degradante ou for&ccedil;ado, na codeiade suprimentos.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">O desenvolvimento da cadeia visa assegurar a continuidade de fornecimento em longo prazo, melhorar o desempenho, promover o desenvolvimento sustent&aacute;vel da pr&oacute;pria cadeia e agregar valor ao neg&oacute;cio.</li>\n\t<li style=\"text-align: justify;\">O desenvolvimento dos fornecedores &#39;locais&#39; visa promover a sustentabilidade, por meio do desenvolvimento econ&ocirc;mico das localidades das instala&ccedil;&otilde;es e do com&eacute;rcio, bem como da redu&ccedil;&atilde;o de impactos ambientais do transporte de mercadorias e de prestadores de servi&ccedil;os.</li>\n\t<li style=\"text-align: justify;\">Os fornecedores e parceiros mencionados nesta quest&atilde;o s&atilde;o aqueles que est&atilde;o envolvidos ou que podem vir a se envolver com a cadeia de suprimento da organiza&ccedil;&atilde;o, fornecendo ou podendo vir a fornecer produtos ou prestar servi&ccedil;os, direta ou indiretamente, para a organiza&ccedil;&atilde;o, ou atuando em nome dela para os seus clientes.</li>\n</ol>\n'),
  (1027,184,'b','<p>Como as necessidades e expectativas dos fornecedores s&atilde;o identificadas, analisadas, traduzidas em requisitos de desempenho e utilizadas para a defini&ccedil;&atilde;o e melhoria das pol&iacute;ticas e dos programas relativos aos fornecedores?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar os principais mecanismos ou canais de intera&ccedil;&atilde;o com os fornecedores.</li>\n</ul>\n','<ul>\n\t<li style=\"text-align:justify\">Apresentar os principais requisitos do fornecedor a serem atendidos e os respectivos indicadores de desempenho.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">As principais necessidades e expectativas dos fornecedores s&atilde;o requeridas no Perfil P1C(1).</li>\n</ol>\n'),
  (1028,184,'c','<p>Como os fornecedores s&atilde;o qualificados e selecionados?</p>\n','<ul>\n\t<li style=\"text-align: justify;\">Destacar os principais crit&eacute;rios utilizados para qualifica&ccedil;&atilde;o e sele&ccedil;&atilde;o;</li>\n\t<li style=\"text-align: justify;\">Destacar as formas utilizadas para estabelecer parcerias com fornecedores, conforme aplic&aacute;vel.</li>\n</ul>\n','',''),
  (1029,184,'d','<p>Como a organiza&ccedil;&atilde;o monitora e avalia o desempenho dos fornecedores, considerando os requisitos de fornecimento?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar as formas de pronta comunica&ccedil;&atilde;o de eventuais n&atilde;o-conformidades e de solicita&ccedil;&atilde;o de a&ccedil;&otilde;es corretivas aos fornecedores.</li>\n</ul>\n','<ul>\n\t<li style=\"text-align:justify\">Apresentar os principais requisitos de fornecimento a serem atendidos pelos fornecedores e os respectivos indicadores de desempenho.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">A avalia&ccedil;&atilde;o e o monitoramento do nlvel de desempenho de fornecedores possibilitam tamb&eacute;m a melhoria dos processos de qualifica&ccedil;&atilde;o e sele&ccedil;&atilde;o.</li>\n</ol>\n'),
  (1030,184,'e','<p>Como a organiza&ccedil;&atilde;o estimula os seus fornecedores a implementar melhorias, inova&ccedil;&otilde;es e otimiza&ccedil;&atilde;o de custos nos seus processos e produtos?</p>\n','','<ul>\n\t<li style=\"text-align: justify;\">Apresentar exemplos de melhorias ou inova&ccedil;&otilde;es implantadas pelos fornecedores.</li>\n</ul>\n',''),
  (1031,184,'f','<p>Como a for&ccedil;a de trabalho dos fornecedores, que atuam diretamente nos processos da cadeia de valor da organiza&ccedil;&atilde;o, &eacute; comprometida com os valores e princ&iacute;pios organizacionais, incluindo os relativos &agrave; responsabilidade socioambiental e &agrave; sa&uacute;de e seguran&ccedil;a, bem como com as diretrizes a eles aplic&aacute;veis?</p>\n','<ul>\n\t<li style=\"text-align: justify;\">Destacar quais pr&aacute;ticas de gest&atilde;o da organiza&ccedil;&atilde;o s&atilde;o aplic&aacute;veis a essas pessoas e quais foram desenvolvidas especificamente para elas.</li>\n</ul>\n','',''),
  (1032,185,'a','<p>Como s&atilde;o definidos os requisitos de desempenho econ&ocirc;mico-financeiro da organiza&ccedil;&atilde;o e monitorados os aspectos que causam impacto na sustentabilidade econ&ocirc;mica do neg&oacute;cio?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar a forma de participa&ccedil;&atilde;o dos s&oacute;cios, mantenedores ou instituidores e dos administradores na defini&ccedil;&atilde;o e monitoramento dos n&iacute;veis esperados de desempenho econ&ocirc;mico-financeiro.</li>\n</ul>\n','<ul>\n\t<li style=\"text-align:justify\">Apresentar os principais requisitos e os respectivos indicadores de desempenho monitorados, incluindo aqueles relativos aos grupos de estrutura, liquidez, atividade e rentabilidade.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">Cada organiza&ccedil;&atilde;o, em fun&ccedil;&atilde;o de seu perfil e estrat&eacute;gias, precisa identificar os par&acirc;metros financeiros que devem ser controlados. Usualmente, s&atilde;o utilizados indicadores, tais como: 1) Estrutura - endividamento (passivo circulante mais exig&iacute;vel de longo prazo dividido pelo patrim&ocirc;nio l&iacute;quido); composi&ccedil;&atilde;o do endividamento (passivo circulante dividido pelo passivo circulante mais exig&iacute;vel de longo prazo); endividamento oneroso (recursos onerosos divididos pelo passivo circulante mais exig&iacute;vel de longo prazo); imobiliza&ccedil;&atilde;o (ativo permanente dividido pelo patrim&ocirc;nio l&iacute;quido); 2) Liquidez - liquidez corrente (ativo circulante dividido pelo passivo circulante); liquidez geral (ativo circulante mais realiz&aacute;vel de longo prazo dividido pelo passivo circulante mais exig&iacute;vel de longo prazo); 3) Atividade - prazo m&eacute;dio de recebimento de vendas; prazo m&eacute;dio de renova&ccedil;&atilde;o de estoques; prazo m&eacute;dio do pagamento de compras; ciclo financeiro (prazo m&eacute;dio de recebimento de vendas mais prazo m&eacute;dio de renova&ccedil;&atilde;o de estoques menos prazo m&eacute;dio do pagamento de compras); e 4) Rentabilidade - giro do ativo (receita l&iacute;quida dividida pelo ativo); rentabilidade do patrim&ocirc;nio l&iacute;quido (lucro l&iacute;quido dividido pelo patrim&ocirc;nio l&iacute;quido); margem bruta (receita de vendas menos o custo dos produtos vendidos dividido pela receita de vendas); vendas (receita de vendas dividida pela receita de vendas prevista); crescimento da receita (total de vendas no per&iacute;odo de um ano dividido pelas vendas no ano anterior). Ainda podem ser inclu&iacute;dos indicadores como: valor econ&ocirc;mico agregado (EVA - lucro l&iacute;quido menos custo de oportunidade do capital empregado); Ebitda; &iacute;ndice de cobertura das despesas financeiras (Ebitda dividido pelas despesas financeiras).</li>\n</ol>\n'),
  (1033,185,'b','<p>Como a organiza&ccedil;&atilde;o assegura os recursos financeiros necess&aacute;rios para atender &agrave;s necessidades operacionais e manter equilibrado o fluxo financeiro?</p>\n','<ul>\n\t<li style=\"text-align: justify;\">Destacar os crit&eacute;rios usados para capta&ccedil;&atilde;o de recursos, concess&atilde;o de cr&eacute;ditos e controle de recebimentos.</li>\n</ul>\n','',''),
  (1034,185,'c','<p>Como a organiza&ccedil;&atilde;o define os recursos financeiros e avalia os investimentos necess&aacute;rios para apoiar as estrat&eacute;gias e planos de a&ccedil;&atilde;o?</p>\n','<ul>\n\t<li style=\"text-align: justify;\">Destacar os crit&eacute;rios utilizados para definir a forma de capta&ccedil;&atilde;o apropriada.</li>\n</ul>\n','',''),
  (1035,185,'d','<p>Como &eacute; elaborado e controlado o or&ccedil;amento?</p>\n','<ul>\n\t<li style=\"text-align:justify\">Destacar a forma utilizada para assegurar o alinhamento entre o or&ccedil;amento e as estrat&eacute;gias e objetivos da organiza&ccedil;&atilde;o.</li>\n</ul>\n','','<ol>\n\t<li style=\"text-align: justify;\">A elabora&ccedil;&atilde;o e o controle do or&ccedil;amento visam assegurar o atendimento dos n&iacute;veis esperados de desempenho financeiro.</li>\n</ol>\n'),
  (1036,186,'a','<p>Apresentar os resultados dos principais indicadores relativos &agrave; gest&atilde;o econ&ocirc;mico-financeira.</p>\n','<ul>\n\t<li style=\"text-align:justify\">Classificar os resultados segundo os grupos de estrutura, liquidez, atividade e rentabilidade, conforme aplic&aacute;vel;</li>\n\t<li style=\"text-align:justify\">Estratificar os resultados por unidades ou filiais mais importantes, quando aplic&aacute;vel.</li>\n</ul>\n','<ul>\n\t<li style=\"text-align:justify\">Para cada indicador apresentado, informar se ele &eacute;:\n\t<ul>\n\t\t<li style=\"text-align:justify\">(E) estrat&eacute;gico,</li>\n\t\t<li style=\"text-align:justify\">(O) operacional; ou</li>\n\t\t<li style=\"text-align:justify\">(E,O) ambos.</li>\n\t</ul>\n\t</li>\n\t<li style=\"text-align:justify\">Mostrar o sentido de favorabilidade de cada s&eacute;rie hist&oacute;rica:\n\t<ul>\n\t\t<li style=\"text-align:justify\">quanto maior, melhor ou;</li>\n\t\t<li style=\"text-align:justify\">quanto menor, melhor ou;</li>\n\t\t<li style=\"text-align:justify\">estabiliza&ccedil;&atilde;o em n&iacute;vel aceit&aacute;vel, i.e., n&iacute;vel suficientemente competitivo ou cumprindo compromisso com requisito de parte interessada, ou ambos, de forma compat&iacute;vel com as estrat&eacute;gias.</li>\n\t</ul>\n\t</li>\n\t<li style=\"text-align:justify\">Esclarecer por que o sentido da favorabilidade da s&eacute;rie hist&oacute;rica &eacute; a estabiliza&ccedil;&atilde;o em n&iacute;vel aceit&aacute;vel, quando for o caso.</li>\n\t<li style=\"text-align:justify\">Esclarecer eventual piora nos resultados, quando poss&iacute;vel.</li>\n\t<li style=\"text-align:justify\">Incluir os referenciais comparativos pertinentes, e a quem se referem, relativos ao &uacute;ltimo exerc&iacute;cio ou ciclo de avalia&ccedil;&atilde;o, para indicadores que permitam compara&ccedil;&otilde;es a fim de avaliar a competitividade no mercado ou setor de atua&ccedil;&atilde;o. Esclarecer, quando poss&iacute;vel, n&iacute;veis de desempenho desfavor&aacute;veis em rela&ccedil;&atilde;o ao referencial comparativo.</li>\n\t<li style=\"text-align:justify\">Incluir os n&iacute;veis de desempenho ou melhorias esperadas que expressem os principais requisitos de partes interessadas, e a que partes se referem, relativas ao &uacute;ltimo exerc&iacute;cio ou ciclo de avalia&ccedil;&atilde;o, para indicadores que s&atilde;o utilizados a fim de avaliar o cumprimento desses requisitos. Esclarecer, quando poss&iacute;vel, o n&atilde;o atendimento a esses requisitos.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">Estes resultados referem-se aos indicadores de desempenho dos processos citados em 7.3, assim como os eventuais indicadores econ&ocirc;mico-financeiros utilizados para avaliar o atendimento de requisitos de controladores e outras partes interessadas, citados em 1.3C, e a implementa&ccedil;&atilde;o das estrat&eacute;gias, em 2.2A.</li>\n\t<li style=\"text-align: justify;\">Ver em 8. Resultados outras orienta&ccedil;&otilde;es gerais para os Itens de Resultados.</li>\n</ol>\n'),
  (1037,187,'a','<p>Apresentar os resultados dos principais indicadores relativos &agrave; responsabilidade socioambiental e ao desenvolvimento social.</p>\n','<ul>\n\t<li style=\"text-align:justify\">Separar os resultados em sociais e ambientais;</li>\n\t<li style=\"text-align:justify\">Estratificar os resultados por instala&ccedil;&otilde;es ou comunidades mais importantes, conforme aplic&aacute;vel.</li>\n</ul>\n','<ul>\n\t<li style=\"text-align:justify\">Para cada indicador apresentado, informar se ele &eacute;:\n\t<ul>\n\t\t<li style=\"text-align:justify\">(E) estrat&eacute;gico,</li>\n\t\t<li style=\"text-align:justify\">(O) operacional; ou</li>\n\t\t<li style=\"text-align:justify\">(E,O) ambos.</li>\n\t</ul>\n\t</li>\n\t<li style=\"text-align:justify\">Mostrar o sentido de favorabilidade de cada s&eacute;rie hist&oacute;rica:\n\t<ul>\n\t\t<li style=\"text-align:justify\">quanto maior, melhor ou;</li>\n\t\t<li style=\"text-align:justify\">quanto menor, melhor ou;</li>\n\t\t<li style=\"text-align:justify\">estabiliza&ccedil;&atilde;o em n&iacute;vel aceit&aacute;vel, i.e., n&iacute;vel suficientemente competitivo ou cumprindo compromisso com requisito de parte interessada, ou ambos, de forma compat&iacute;vel com as estrat&eacute;gias.</li>\n\t</ul>\n\t</li>\n\t<li style=\"text-align:justify\">Esclarecer por que o sentido da favorabilidade da s&eacute;rie hist&oacute;rica &eacute; a estabiliza&ccedil;&atilde;o em n&iacute;vel aceit&aacute;vel, quando for o caso.</li>\n\t<li style=\"text-align:justify\">Esclarecer eventual piora nos resultados, quando poss&iacute;vel.</li>\n\t<li style=\"text-align:justify\">Incluir os referenciais comparativos pertinentes, e a quem se referem, relativos ao &uacute;ltimo exerc&iacute;cio ou ciclo de avalia&ccedil;&atilde;o, para indicadores que permitam compara&ccedil;&otilde;es a fim de avaliar a competitividade no mercado ou setor de atua&ccedil;&atilde;o. Esclarecer, quando poss&iacute;vel, n&iacute;veis de desempenho desfavor&aacute;veis em rela&ccedil;&atilde;o ao referencial comparativo.</li>\n\t<li style=\"text-align:justify\">Incluir os n&iacute;veis de desempenho ou melhorias esperadas que expressem os principais requisitos de partes interessadas, e a que partes se referem, relativas ao &uacute;ltimo exerc&iacute;cio ou ciclo de avalia&ccedil;&atilde;o, para indicadores que s&atilde;o utilizados a fim de avaliar o cumprimento desses requisitos. Esclarecer, quando poss&iacute;vel, o n&atilde;o atendimento a esses requisitos.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">Estes resultados referem-se aos indicadores de desempenho dos processos citados no Crit&eacute;rio 4, assim como os eventuais indicadores sociais e ambientais utilizados para avaliar o atendimento de requisitos de controladores e outras partes interessadas, citados em 1.3C, e a implementa&ccedil;&atilde;o das estrat&eacute;gias, em 2.2A.</li>\n\t<li style=\"text-align: justify;\">Ver em 8. Resultados outras orienta&ccedil;&otilde;es gerais para os Itens de Resultados.</li>\n</ol>\n'),
  (1038,188,'a','<p>Apresentar os resultados dos principais indicadores relativos aos clientes e aos mercados-aIvo incluindo os referentes &agrave; imagem da organiza&ccedil;&atilde;o.</p>\n','<ul>\n\t<li style=\"text-align:justify\">Estratificar os resultados por segmentos de mercado, grupos de clientes ou tipos de produtos mais importantes, conforme aplic&aacute;vel.</li>\n</ul>\n','<ul>\n\t<li style=\"text-align:justify\">Para cada indicador apresentado, informar se ele &eacute;:\n\t<ul>\n\t\t<li style=\"text-align:justify\">(E) estrat&eacute;gico,</li>\n\t\t<li style=\"text-align:justify\">(O) operacional; ou</li>\n\t\t<li style=\"text-align:justify\">(E,O) ambos.</li>\n\t</ul>\n\t</li>\n\t<li style=\"text-align:justify\">Mostrar o sentido de favorabilidade de cada s&eacute;rie hist&oacute;rica:\n\t<ul>\n\t\t<li style=\"text-align:justify\">quanto maior, melhor ou;</li>\n\t\t<li style=\"text-align:justify\">quanto menor, melhor ou;</li>\n\t\t<li style=\"text-align:justify\">estabiliza&ccedil;&atilde;o em n&iacute;vel aceit&aacute;vel, i.e., n&iacute;vel suficientemente competitivo ou cumprindo compromisso com requisito de parte interessada, ou ambos, de forma compat&iacute;vel com as estrat&eacute;gias.</li>\n\t</ul>\n\t</li>\n\t<li style=\"text-align:justify\">Esclarecer por que o sentido da favorabilidade da s&eacute;rie hist&oacute;rica &eacute; a estabiliza&ccedil;&atilde;o em n&iacute;vel aceit&aacute;vel, quando for o caso.</li>\n\t<li style=\"text-align:justify\">Esclarecer eventual piora nos resultados, quando poss&iacute;vel.</li>\n\t<li style=\"text-align:justify\">Incluir os referenciais comparativos pertinentes, e a quem se referem, relativos ao &uacute;ltimo exerc&iacute;cio ou ciclo de avalia&ccedil;&atilde;o, para indicadores que permitam compara&ccedil;&otilde;es a fim de avaliar a competitividade no mercado ou setor de atua&ccedil;&atilde;o. Esclarecer, quando poss&iacute;vel, n&iacute;veis de desempenho desfavor&aacute;veis em rela&ccedil;&atilde;o ao referencial comparativo.</li>\n\t<li style=\"text-align:justify\">Incluir os n&iacute;veis de desempenho ou melhorias esperadas que expressem os principais requisitos de partes interessadas, e a que partes se referem, relativas ao &uacute;ltimo exerc&iacute;cio ou ciclo de avalia&ccedil;&atilde;o, para indicadores que s&atilde;o utilizados a fim de avaliar o cumprimento desses requisitos. Esclarecer, quando poss&iacute;vel, o n&atilde;o atendimento a esses requisitos.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">Estes resultados referem-se aos indicadores de desempenho dos processos citados no Crit&eacute;rio 3. assim como os eventuais indicadores relativos a clientes utilizados para avaliar o atendimento de requisitos de controladores e outras partes interessadas, citados em 1.3C, e a implementa&ccedil;&atilde;o das estrat&eacute;gias, em 2.2A.</li>\n\t<li style=\"text-align: justify;\">Ver em 8. Resultados outras orienta&ccedil;&otilde;es gerais para os Itens de Resultados.</li>\n</ol>\n'),
  (1039,189,'a','<p>Apresentar os resultados dos principais indicadores relativos a pessoas, incluindo os referentes ao sistema de trabalho, capacita&ccedil;&atilde;o e desenvolvimento e qualidade de vida, bem como os referentes &agrave; lideran&ccedil;a, comunica&ccedil;&atilde;o e cultura.</p>\n','<ul>\n\t<li style=\"text-align:justify\">Estratificar os resultados por fun&ccedil;&otilde;es na organiza&ccedil;&atilde;o ou grupos de pessoas da for&ccedil;a de trabalho e, conforme aplic&aacute;vel, por instala&ccedil;&otilde;es mais importantes.</li>\n</ul>\n','<ul>\n\t<li style=\"text-align:justify\">Para cada indicador apresentado, informar se ele &eacute;:\n\t<ul>\n\t\t<li style=\"text-align:justify\">(E) estrat&eacute;gico,</li>\n\t\t<li style=\"text-align:justify\">(O) operacional; ou</li>\n\t\t<li style=\"text-align:justify\">(E,O) ambos.</li>\n\t</ul>\n\t</li>\n\t<li style=\"text-align:justify\">Mostrar o sentido de favorabilidade de cada s&eacute;rie hist&oacute;rica:\n\t<ul>\n\t\t<li style=\"text-align:justify\">quanto maior, melhor ou;</li>\n\t\t<li style=\"text-align:justify\">quanto menor, melhor ou;</li>\n\t\t<li style=\"text-align:justify\">estabiliza&ccedil;&atilde;o em n&iacute;vel aceit&aacute;vel, i.e., n&iacute;vel suficientemente competitivo ou cumprindo compromisso com requisito de parte interessada, ou ambos, de forma compat&iacute;vel com as estrat&eacute;gias.</li>\n\t</ul>\n\t</li>\n\t<li style=\"text-align:justify\">Esclarecer por que o sentido da favorabilidade da s&eacute;rie hist&oacute;rica &eacute; a estabiliza&ccedil;&atilde;o em n&iacute;vel aceit&aacute;vel, quando for o caso.</li>\n\t<li style=\"text-align:justify\">Esclarecer eventual piora nos resultados, quando poss&iacute;vel.</li>\n\t<li style=\"text-align:justify\">Incluir os referenciais comparativos pertinentes, e a quem se referem, relativos ao &uacute;ltimo exerc&iacute;cio ou ciclo de avalia&ccedil;&atilde;o, para indicadores que permitam compara&ccedil;&otilde;es a fim de avaliar a competitividade no mercado ou setor de atua&ccedil;&atilde;o. Esclarecer, quando poss&iacute;vel, n&iacute;veis de desempenho desfavor&aacute;veis em rela&ccedil;&atilde;o ao referencial comparativo.</li>\n\t<li style=\"text-align:justify\">Incluir os n&iacute;veis de desempenho ou melhorias esperadas que expressem os principais requisitos de partes interessadas, e a que partes se referem, relativas ao &uacute;ltimo exerc&iacute;cio ou ciclo de avalia&ccedil;&atilde;o, para indicadores que s&atilde;o utilizados a fim de avaliar o cumprimento desses requisitos. Esclarecer, quando poss&iacute;vel, o n&atilde;o atendimento a esses requisitos.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">Estes resultados referem-se aos indicadores de desempenho dos processos citados no Crit&eacute;rio 6, assim como os eventuais indicadores relativos &agrave;s pessoas utilizados para avaliar o atendimento de requisitos de controladores e outras partes interessadas, citados em 1.2C, e a implementa&ccedil;&atilde;o das estrat&eacute;gias, em 2.2A.</li>\n\t<li style=\"text-align: justify;\">As fun&ccedil;&otilde;es na organiza&ccedil;&atilde;o podem abranger as fun&ccedil;&otilde;es operacionais, t&eacute;cnicas, administrativas e de lideran&ccedil;a ou outra classifica&ccedil;&atilde;o adotada pela organiza&ccedil;&atilde;o.</li>\n\t<li style=\"text-align: justify;\">Os grupos de pessoas da for&ccedil;a de trabalho podem abranger os contratados em tempo integral ou parcial, horistas, mensalistas, tempor&aacute;rios, aut&ocirc;nomos e contratados de terceiros que trabalham sob coordena&ccedil;&atilde;o direta da organiza&ccedil;&atilde;o ou outros grupos com gest&atilde;o diferenciada.</li>\n\t<li style=\"text-align: justify;\">Ver em 8. Resultados outras orienta&ccedil;&otilde;es gerais para os Itens de Resultados.</li>\n</ol>\n'),
  (1040,190,'a','<p>Apresentar os resultados dos principais indicadores relativos a produtos, aos processos da cadeia de valor, a fornecedores e a processos de gest&atilde;o transversais n&atilde;o relacionados aos demais itens deste Crit&eacute;rio.</p>\n','<ul>\n\t<li style=\"text-align:justify\">Separar os resultados de produtos, de processos da cadeia de valor e relativos a fornecedores mais importantes e, caso haja, os relativos a processos de gest&atilde;o transversais.</li>\n</ul>\n','<ul>\n\t<li style=\"text-align:justify\">Para cada indicador apresentado, informar se ele &eacute;:\n\t<ul>\n\t\t<li style=\"text-align:justify\">(E) estrat&eacute;gico,</li>\n\t\t<li style=\"text-align:justify\">(O) operacional; ou</li>\n\t\t<li style=\"text-align:justify\">(E,O) ambos.</li>\n\t</ul>\n\t</li>\n\t<li style=\"text-align:justify\">Mostrar o sentido de favorabilidade de cada s&eacute;rie hist&oacute;rica:\n\t<ul>\n\t\t<li style=\"text-align:justify\">quanto maior, melhor ou;</li>\n\t\t<li style=\"text-align:justify\">quanto menor, melhor ou;</li>\n\t\t<li style=\"text-align:justify\">estabiliza&ccedil;&atilde;o em n&iacute;vel aceit&aacute;vel, i.e., n&iacute;vel suficientemente competitivo ou cumprindo compromisso com requisito de parte interessada, ou ambos, de forma compat&iacute;vel com as estrat&eacute;gias.</li>\n\t</ul>\n\t</li>\n\t<li style=\"text-align:justify\">Esclarecer por que o sentido da favorabilidade da s&eacute;rie hist&oacute;rica &eacute; a estabiliza&ccedil;&atilde;o em n&iacute;vel aceit&aacute;vel, quando for o caso.</li>\n\t<li style=\"text-align:justify\">Esclarecer eventual piora nos resultados, quando poss&iacute;vel.</li>\n\t<li style=\"text-align:justify\">Incluir os referenciais comparativos pertinentes, e a quem se referem, relativos ao &uacute;ltimo exerc&iacute;cio ou ciclo de avalia&ccedil;&atilde;o, para indicadores que permitam compara&ccedil;&otilde;es a fim de avaliar a competitividade no mercado ou setor de atua&ccedil;&atilde;o. Esclarecer, quando poss&iacute;vel, n&iacute;veis de desempenho desfavor&aacute;veis em rela&ccedil;&atilde;o ao referencial comparativo.</li>\n\t<li style=\"text-align:justify\">Incluir os n&iacute;veis de desempenho ou melhorias esperadas que expressem os principais requisitos de partes interessadas, e a que partes se referem, relativas ao &uacute;ltimo exerc&iacute;cio ou ciclo de avalia&ccedil;&atilde;o, para indicadores que s&atilde;o utilizados a fim de avaliar o cumprimento desses requisitos. Esclarecer, quando poss&iacute;vel, o n&atilde;o atendimento a esses requisitos.</li>\n</ul>\n','<ol>\n\t<li style=\"text-align: justify;\">Estes resultados referem-se aos indicadores de desempenho dos processos citados em 7.1, compat&iacute;veis com o Perfil PI B(l/2), e em 7.3, compat&iacute;veis com P1D{4), assim como os eventuais indicadores de processos utilizados para avaliar o atendimento de requisitos de controladores e outras partes interessadas, citados em 1.2C, e a implementa&ccedil;&atilde;o das estrat&eacute;gias, em 2.2A.</li>\n\t<li style=\"text-align: justify;\">Incluem tamb&eacute;m os resultados de indicadores de desempenho de sistemas de gest&atilde;o transversais, como de desenvolvimento da gest&atilde;o, governan&ccedil;a, atua&ccedil;&atilde;o em rede, an&aacute;lise do desempenho, planejamento, informa&ccedil;&otilde;es, conhecimento, e similares, porventura utilizados pela organiza&ccedil;&atilde;o.</li>\n\t<li style=\"text-align: justify;\">Ver em 8. Resultados outras orienta&ccedil;&otilde;es gerais para os Itens de Resultados.</li>\n</ol>\n');
