  CONSTRAINT pratica_indicador_evidencia_indicador FOREIGN KEY (pratica_indicador_evidencia_indicador) REFERENCES pratica_indicador (pratica_indicador_id) ON DELETE CASCADE ON UPDATE CASCADE
