INSERT INTO pratica_modelo (pratica_modelo_id, pratica_modelo_nome, pratica_modelo_pontos, pratica_modelo_obs, pratica_modelo_tipo, pratica_modelo_ordem) VALUES 
  (10,'FNQ 2015 250 pontos',250,'<p>Aplicáveis às organizações que estão em estágios iniciais de evolução do seu sistema de gestão e começando a medir e perceber melhorias nos seus resultados.</p>','fnq_2015',8),
  (11,'FNQ 2015 500 pontos',500,'<p>Aplicáveis às organizações cujo sistema de gestão está em franca evolução e já demonstram competitividade e atendimento às expectativas de partes interessadas em vários resultados.</p>','fnq_2015',9),
  (12,'FNQ 2015 1000 pontos',1000,'<p>aplicáveis às organizações que têm um sistema de gestão bastante evoluído, já demonstram excelência em alguns resultados, competitividade na maioria e pleno atendimento às expectativas de partes interessadas em quase todos eles.</p>','fnq_2015',10);
