INSERT INTO pratica_criterio (pratica_criterio_id, pratica_criterio_modelo, pratica_criterio_numero, pratica_criterio_nome, pratica_criterio_pontos, pratica_criterio_obs, pratica_criterio_orientacao, pratica_criterio_resultado) VALUES 
 	(73,10,1,'Liderança',33,'<p>Este Critério aborda processos gerenciais relativos à cultura organizacional e desenvolvimento da gestão, governança, exercício da liderança e análise do desempenho da organização.</p>',NULL,0),
  (74,10,2,'Estratégias e Planos',20,'<p>Este Critério aborda processos gerenciais relativos à formulação e implementação das estratégias.</p>',NULL,0),
  (75,10,3,'Clientes',20,'<p>Este Critério aborda processos gerenciais relativos à análise e desenvolvimento de mercado e ao relacionamento com clientes.</p>',NULL,0),
  (76,10,4,'Sociedade',	12,'<p>Este Critério aborda processos gerenciais relativos à responsabilidade socioambiental e ao desenvolvimento social.</p>',NULL,0),
  (77,10,5,'Informações e Conhecimento',	12,'<p>Este Critério aborda processos gerenciais relativos às informações da organização e ao conhecimento organizacional.</p>',NULL,0),
  (78,10,6,'Pessoas',20,'<p>Este Critério aborda processos gerenciais relativos aos sistemas de trabalho, à capacitação e desenvolvimento e à qualidade de vida.</p>',NULL,0),
  (79,10,7,'Processos',33,'<p>Este Critério aborda processos gerenciais relativos aos processos da cadeia de valor, processos relativos a fornecedores e processos econômico-financeiros.</p>',NULL,0),
  (80,10,8,'Resultados',100,'<p>Este critério aborda a apresentação de resultados estratégicos e operacionais relevantes para a organização, na forma de indicadores que permitam avaliar, no conjunto, a melhoria dos resultados, o nível de competitividade e o cumprimento de compromissos com requisitos de partes interessadas, nas perspectivas econômico-financeira, socioambiental e relativas a clientes e mercados, a pessoas e aos processos da cadeia de valor.</p>',NULL,1),
  (81,11,1,'Liderança',55,'<p>Este Critério aborda processos gerenciais relativos à cultura organizacional e desenvolvimento da gestão, governança, exercício da liderança e análise do desempenho da organização.</p>',NULL,0),
