INSERT INTO modelos_tipo (modelo_tipo_id, modelo_tipo_nome, modelo_tipo_campos, descricao, imagem, organizacao) VALUES 
  (1,'Parte',,'Correspondência que tramita no âmbito de uma OM, por meio da qual o militar se comunica com um de seus pares ou superior hierárquico, em objeto de serviço, podendo ser utilizado suporte eletrônico (o documento é arquivado/enviado por rede de computadores ou por meio disquete, CD ROM, etc, porém continua sendo uma parte), ou ser substituída por mensagem eletrônica, sempre que houver meios físicos adequados;','parte.gif',1),
  (2,'Ofício',,'O Ofício Interno à Força destina-se à comunicação entre Organizações Militares. Forma de correspondência utilizada pela autoridade militar, com a finalidade de tratar de assuntos oficiais, podendo ser utilizado suporte eletrônico (o documento é arquivado/enviado por rede de computadores ou por meio de disquete, CD ROM, etc, porém continua sendo um o?cio), ou ser substituída por mensagem eletrônica, sempre que houver meios físicos adequados;','oficio_restrito.gif',1),
  (3,'MDO',,'Forma de correspondência enviada por estafeta, podendo ser substituída por mensagem eletrônica, sempre que houver meios físicos adequados;','mdo.gif',1),
  (4,'Requerimento',,'Documento em que o signatário pede à autoridade competente o reconhecimento ou a concessão de direito que julga possuir, amparado na legislação que regula o objeto pretendido;','requerimento.gif',1),
  (5,'Encaminhamento',,'Ato exarado, de forma simplificada e sucinta, em um documento em trânsito ou em folha anexa ao mesmo:<ul><li>para providências, conhecimento, complementação da instrução nele contida ou como informação;</li><li>caso o documento em questão seja eletrônico, o encaminhamento poderá ter suporte semelhante;</li><li>pode, também, no âmbito da Força, substituir ofícios de mero encaminhamento;</li></ul>','encaminhamento.gif',1),
  (6,'Fax',,'Documento produzido para fac-simile, utilizado para transmissão de mensagens e envio antecipado de documentos, de cujo conhecimento há premência de tempo, sendo, também, um meio de comunicação, podendo ser substituído por mensagem eletrônica, sempre que houver meios físicos adequados;','fax.gif',1),
  (7,'Nota',,'Documento proposto por uma autoridade subordinada competente, sujeito à aprovação do comandante, chefe ou diretor, para fim de publicação em seu Bl, podendo ser utilizado suporte eletrônico (o documento é arquivado/enviado por rede de computadores ou por meio de disquete, CD ROM, etc, porém continua sendo uma nota para boletim), ou ser substituído por mensagem eletrônica, sempre que houver meios físicos adequados;','nota_bi.gif',1),
  (8,'Informação',,'A informação sobre o requerente segue as disposições estabelecidas e deve possuir:<br><ul><li>cabeçalho.</li><li>texto.</li><li>o amparo do requerente - apenas citar o enquadramento completo do texto legal sobre o assunto, que constitua o fundamento legal da pretensão;</li><li>o estudo fundamentado - onde são apreciados os dados informativos sobre o requerente, verificando-se a coerência entre o que é requerido e o amparo legal;</li<li>parecer - nos escalões de comando diferentes daquele a quem é dirigido orequerimento, caberão apenas dois tipos de pareceres básicos, o encaminhamento ou o arquivamento, conforme exemplificado a seguir:<br><br>Todo requerimento que satisfizer às exigências legais sairá da OM de origem instruído com as informações e os documentos necessários ao seu estudo e à decisão, sendo encaminhado à autoridade competente (despacho final), por meio do escalão de comando.</li></ul>','info_requerimento.gif',1),
  (9,'Restituição',,'Ato exarado em um documento ou em folha anexa ao mesmo, devolvendo-o, seja para solicitar esclarecimento, seja por havê-lo prestado:<br><ul><li>tem forma sucinta;</li><li>limita-se a justificar o motivo da providência tomada ou solicitada;</li><li>caso o documento em questão seja eletrônico, a restituição poderá ter suporte semelhante;</li><li>pode, também, no âmbito da Força, substituir oficios de mera restituição;Ato exarado, de forma simplificada e sucinta, em um documento em trânsito ou em folha anexa ao mesmo:<ul><li>para providências, conhecimento, complementação da instrução nele contida ou como informação;</li><li>caso o documento em questão seja eletrônico, o encaminhamento poderá ter suporte semelhante;</li><li>pode, também, no âmbito da Força, substituir ofícios de mero encaminhamento;</li></ul></li></ul>','restituicao.gif',1),
  (10,'Relatório',,'Expediente contendo exposição minuciosa de fatos ou atividades que devam ser apreciados por autoridade competente e modelos para elaboração deste documento são encontrados em regulamentos, manuais e instruções especilicas, podendo ser utilizado suporte eletrônico (o documento é arquivadolenviado por rede de computadores ou por meio de disquete, CD ROM, etc, porém continua sendo um relatório), sempre que houver meios físicos adequados;','relatorio.gif',1),
  (11,'Memorando',,'Correspondência que circula no âmbito de uma OM, utilizada por autoridade superior para transmissão de ordens, instruções, decisões, recomendações, esclarecimentos ou informações:<br><ul><li>a. tem caráter eminentemente intemo e como principal caracteristica a agilidade, devendo sua tramitação pautar-se pela rapidez e pela simplicidade dos procedimentos burocráticos; e</li><li>b. pode ser utilizado suporte eletrônico (o documento é arquivadolenviado por rede de computadores ou por meio de disquete, CD ROM, etc, porém continua sendo um memorando), ou ser substituído por mensagem eletrônica, sempre que houver meios físicos adequados;</li></ul>','memorando.gif',1),
  (12,'Remessa',,'Ato exarado em um documento em trânsito ou em folha anexa ao mesmo, submetendo-o à apreciação de autoridade competente:<br><ul><li>tem forma sucinta, limitando-se a justificar o motivo da providência tomada ou solicitada;</li><li>caso o documento em questão seja eletrônico, a remessa poderá ter suporte semelhante;</li><li>pode, também, no âmbito da Força, substituir ofícios de simples remessa;</li></ul>','remessa.gif',1),
  (13,'Radiograma',,'Destinado à rápida comunicação entre as partes interessadas, transmitido por meio de equipamentos de telecomunicações, em linguagem e abreviações específícas, podendo ser substituído por mensagem eletrônica, sempre que houver meios físicos adequados;','radiograma.gif',1),
  (14,'Oficío Externo',,'O Oficio Extemo à Força destina-se a particulares ou a autoridades lotadas em organizações não integrantes da Força.<br><br>Forma de correspondência utilizada pela autoridade militar, com a finalidade de tratar de assuntos oficiais, podendo ser utilizado suporte eletrônico (o documento é arquivado/enviado por rede de computadores ou por meio de disquete, CD ROM, etc, porém continua sendo um oficio), ou ser substituída por mensagem eletrônica, sempre que houver meios fisicos adequados;','oficio_particular.gif',1),
  (15,'Documento Externo',,'Correspondência que tramita no âmbito de uma OM, por meio da qual o militar se comunica com um de seus pares ou superior hierárquico, em objeto de serviço, podendo ser utilizado suporte eletrônico (o documento é arquivado/enviado por rede de computadores ou por meio disquete, CD ROM, etc, porém continua sendo uma parte), ou ser substituída por mensagem eletrônica, sempre que houver meios físicos adequados;','externo.gif',1);
