INSERT INTO plano_gestao (pg_id, pg_nome, pg_usuario, pg_cia, pg_dept, pg_usuario_ultima_alteracao, pg_descricao, pg_ano, pg_inicio, pg_fim, pg_modelo, pg_estrut_org, pg_fornecedores, pg_ultima_alteracao, pg_processos_apoio, pg_processos_finalistico, pg_produtos_servicos, pg_clientes, pg_posgraduados, pg_graduados, pg_nivelmedio, pg_nivelfundamental, pg_semescolaridade, pg_pessoalinterno, pg_programas_acoes, pg_premiacoes, pg_acesso, pg_cor, pg_ativo) VALUES
  (1,'Planejamento 2016',6,1,NULL,25,NULL,2016,'2016-01-01','2016-12-31',NULL,'<p>Conforme quadro em anexo é constituída por:<br />Seções do EM: SCmt, S1,S2,S3,S4 e Fisc Adm.<br />SU: CCAp, Cia Com PC, Cia Com PCR e Cia Com Nodal.</p>',NULL,'2016-04-26 13:27:13','<p>Gerenciamento administrativo - inclusão e descarga de material, variações patrimoniais e sindicâncias sobre problemas com material.<br /><br />Gerenciamento de pessoal - alterações, movimentações, pagamento, procedimentos administrativos e boletim interno.<br /><br />Gerenciamento de material - almoxarifado, recebimento, distribuição, recolhimento e guias de quitação.<br /><br />Gerenciamento de saúde - inspeções, atendimento médico e odontológico, evacuação de doentes e atestado de origem.<br /><br />Aprovisionamento - confecção de gêneros alimentícios, controle de material de rancho e saque de etapas.<br /><br />Gerenciamento financeiro - controle de gastos, contas, recursos e fornecedores.<br /><br />Gerenciamento de comunicação social - relações públicas, pesquisas e atendimento ao público externo.</p>','<p>Planejamento operacional;<br />Instrução individual;<br />Adestramento  do efetivo profissional;<br />Cursos e estágios;<br />Garantia da Lei e da Ordem;<br />Ações subsidiárias;<br />Apoio de comunicações ao CMS;<br />Ações cívico-sociais.</p>','<p>Reservista de primeira categoria;<br />Atendimento às necessidades de comunicações do escalão superior;<br />Emprego da tropa em GLO e em defesa externa;<br />Bom nível de preparo da tropa</p>','<p>COMANDO MILITAR DO SUL<br />SOCIEDADE<br />PUBLICO INTERNO E EXTERNO</p>',0,0,0,0,0,NULL,'<p>Projeto soldado-cidadão (Programa do Primeiro Emprego);<br />PROFESP - programa Força no Esporte</p>',NULL,0,'FFFFFF',1);
INSERT INTO plano_gestao2 (pg_id, pg_missao, pg_missao_esc_superior, pg_visao_futuro, pg_visao_futuro_detalhada, pg_ponto_forte, pg_oportunidade_melhoria, pg_oportunidade, pg_ameaca, pg_principio, pg_diretriz_superior, pg_diretriz, pg_objetivo_estrategico, pg_fator_critico, pg_estrategia, pg_meta) VALUES
  (1,'<p>Instalar, explorar e manter a estrutura de comunica&ccedil;&otilde;es que d&ecirc; suporte &agrave;s necessidades dos sistemas operacionais do Comando Militar do Sul, realizando a integra&ccedil;&atilde;o dos meios e processos necess&aacute;rios ao pleno funcionamento do sistema de comando e controle, formando e adestrando o pessoal militar como tropa de comunica&ccedil;&otilde;es em miss&otilde;es de defesa externa territorial e na Garantia da Lei e da Ordem.</p>','<p>Instalar, explorar e manter os sistemas de comunica&ccedil;&otilde;es do Comando Militar do Sul</p>','<p>Instalar, explorar e manter a estrutura de comunica&ccedil;&otilde;es que d&ecirc; suporte &agrave;s necessidades dos sistemas operacionais do Comando Militar do Sul, realizando a integra&ccedil;&atilde;o dos meios e processos necess&aacute;rios ao pleno funcionamento do sistema de comando e controle, formando e adestrando o pessoal militar como tropa de comunica&ccedil;&otilde;es em miss&otilde;es de defesa externa territorial e na Garantia da Lei e da Ordem.</p>','<p>a) se constitu&iacute;do em Comando do Teatro de Opera&ccedil;&otilde;es Terrestre (COMTOT), conduzir,<br />em sua &aacute;rea de responsabilidade, a campanha militar terrestre para derrotar o inimigo que agredir ou amea&ccedil;ar a soberania, a integridade territorial, o patrim&ocirc;nio e os interesses vitais do Brasil.<br />b) se localizado no espa&ccedil;o geogr&aacute;fico sob jurisdi&ccedil;&atilde;o nacional n&atilde;o inclu&iacute;do no Teatro de<br />Opera&ccedil;&otilde;es (TO), constituir um Comando de Zona de Defesa (ZD), combinado ou n&atilde;o, destinado a realizar opera&ccedil;&otilde;es de Defesa Territorial contra a&ccedil;&otilde;es inimigas, bem como, garantir a seguran&ccedil;a de sua respectiva ZD.<br />b. A fim de possibilitar ao Ex&eacute;rcito contribuir para a garantia dos poderes constitucionais, da lei e da ordem:<br />- manter-se em condi&ccedil;&otilde;es de ser empregado, em sua &aacute;rea de responsabilidade, na forma da lei, em situa&ccedil;&atilde;o emergencial e tempor&aacute;ria, ap&oacute;s esgotados os instrumentos destinados &agrave; preserva&ccedil;&atilde;o da ordem p&uacute;blica e da incolumidade das pessoas e do patrim&ocirc;nio, relacionados no art. 144 da Constitui&ccedil;&atilde;o.<br />c. Ficar em condi&ccedil;&otilde;es de participar de opera&ccedil;&otilde;es internacionais, de acordo com os<br />interesses do Pa&iacute;s.<br />d. Como a&ccedil;&atilde;o subsidi&aacute;ria geral, participar do desenvolvimento nacional e da defesa civil, na forma da Lei.<br />e. Como a&ccedil;&atilde;o subsidi&aacute;ria particular, atuar na faixa de fronteira terrestre contra delitos<br />transfronteiri&ccedil;os e ambientais, na forma da Lei.</p>','','','','',NULL,'','','','','','');
