INSERT INTO pratica_requisito (pratica_id, ano, pratica_oque, pratica_onde, pratica_quando, pratica_como, pratica_porque, pratica_quanto, pratica_quem, pratica_descricao, pratica_controlada, pratica_justificativa_controlada, pratica_proativa, pratica_justificativa_proativa, pratica_abrange_pertinentes, pratica_justificativa_abrangencia, pratica_continuada, pratica_justificativa_continuada, pratica_refinada, pratica_justificativa_refinada, pratica_coerente, pratica_justificativa_coerente, pratica_interrelacionada, pratica_justificativa_interrelacionada, pratica_cooperacao, pratica_justificativa_cooperacao, pratica_cooperacao_partes, pratica_justificativa_cooperacao_partes, pratica_arte, pratica_justificativa_arte, pratica_inovacao, pratica_justificativa_inovacao, pratica_melhoria_aprendizado, pratica_justificativa_melhoria_aprendizado) VALUES
  (1,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p style=\"text-align: justify;\">Desde sua cria&ccedil;&atilde;o em 1976, as decis&otilde;es relativas aos principais processos s&atilde;o tomadas pelo Diretor, consoante a Miss&atilde;o e Vis&atilde;o de Futuro, e em conson&acirc;ncia com os Decretos, Portarias, Leis, Normas e Instru&ccedil;&otilde;es em vigor, diretrizes do Escal&atilde;o Superior e baseando-se no assessoramento de todos os componentes da Alta Administra&ccedil;&atilde;o(Estado Maior), que buscam opinar, preferencialmente em suas respectivas &aacute;reas de atua&ccedil;&atilde;o, alicer&ccedil;ados em suas experi&ecirc;ncias profissionais, legisla&ccedil;&atilde;o vigente e com base em dados provenientes de consultas informais realizadas com o p&uacute;blico interno e/ou externo.</p><p style=\"text-align: justify;\">Na Organização as principais decis&otilde;es s&atilde;o tomadas por meio de procedimentos (interligados ou n&atilde;o):<br />&nbsp;&nbsp; 1) decorrente de orienta&ccedil;&otilde;es do Escal&atilde;o Superior e do que prescrevem as Leis e Regulamentos em vigor;<br />&nbsp;&nbsp; 2) por meio da experi&ecirc;ncia pessoal do Cmt da Organização assessorado pelo EM, na busca da melhoria e/ou aperfei&ccedil;oamento de<br />determinada atividade;<br />&nbsp;&nbsp; 3) Os Cmt SU e integrantes do Estado-Maior Geral e Especial, despacham diretamente com o Cmt, diariamente ou sempre que necess&aacute;rio, levando propostas e subs&iacute;dios para que o Cmt tomes as decis&otilde;es relativas aos principais processos da Organização;<br />&nbsp;&nbsp; 4) atrav&eacute;s de reuni&otilde;es do EM (reuni&otilde;es de Comando e Administrativas) em que o assunto &eacute; exposto para todos, a fim de que se troquem informa&ccedil;&otilde;es, e adotando-se uma linha de a&ccedil;&atilde;o;<br />&nbsp;&nbsp; 5) o militar respons&aacute;vel por alguma atividade espec&iacute;fica, que poder&aacute; trazer reflexos para todo a Organização, trata diretamente com o Cmt, exp&otilde;e todas as informa&ccedil;&otilde;es necess&aacute;rias e o Diretor emite sua decis&atilde;o.<br />&nbsp;&nbsp; Diariamente, s&atilde;o realizadas reuni&otilde;es com o Estado-Maior Geral e Especial e com os Comandantes de Subunidades para tratar das atividades do dia; e, semanalmente, realiza-se reuni&otilde;es de Comando para tratar de assuntos atinentes aos principais processos da Organização, e divulga&ccedil;&atilde;o da agenda de atividades.<br />&nbsp;&nbsp; O processo ocorre h&aacute; mais de 10 anos, uma vez que a metodologia &eacute; corporativa.</p>',1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,1,NULL,1,NULL,1,NULL,0,NULL,0,NULL,0,NULL,1,NULL),
  (2,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p style=\"text-align: justify;\">As decis&otilde;es relativas ao Plano de Gest&atilde;o s&atilde;o tomadas pelo Cmt assessorado pelo EM e comandantes de companhias desde 2006, com a implanta&ccedil;&atilde;o do PEG na Organização, observando a Miss&atilde;o e a Vis&atilde;o de Futuro, as diretrizes e o Plano de Gest&atilde;o do Escal&atilde;o Superior, bem como as peculiaridades da tropa de Comunica&ccedil;&otilde;es.<br />&nbsp;&nbsp; Desde 2007, &eacute; realizada uma reuni&atilde;o anual envolvendo o Dirertor e EM da Organização, para defini&ccedil;&atilde;o dos Objetivos Estrat&eacute;gicos, Estrat&eacute;gias e Metas e os respectivos respons&aacute;veis (gerentes) para o ano corrente. Ap&oacute;s a defini&ccedil;&atilde;o das estrat&eacute;gias e metas, os gerentes realizam o seu planejamento e confeccionam os planos de a&ccedil;&atilde;o.<br />&nbsp;&nbsp; Todos os dados s&atilde;o compilados pelo Assessor de Gest&atilde;o, que, ap&oacute;s a aprova&ccedil;&atilde;o do Dirertor da Organização, confecciona o Plano de Gest&atilde;o da Organização.</p>',1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,0,NULL,1,NULL),
  (3,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p style=\"text-align: justify;\">Desde 1976, as principais decis&otilde;es s&atilde;o tomadas pelo Cmt e seu Estado-Maior, nas reuni&otilde;es di&aacute;rias ou em reuni&otilde;es espec&iacute;ficas, que a partir da&iacute;, s&atilde;o retransmitidas posteriormente aos seus subordinados.</p><p style=\"margin-left: 35.45pt;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>',1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,1,NULL,1,NULL,0,NULL,1,NULL,0,NULL,0,NULL,0,NULL),
  (4,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p style=\"text-align: justify;\">Desde 2006, ap&oacute;s a atualiza&ccedil;&atilde;o anual do Plano de Gest&atilde;o, a implementa&ccedil;&atilde;o das decis&otilde;es tomadas ocorre principalmente por meio da execu&ccedil;&atilde;o das ordens emanadas pela Cmt da Organização. O SCmt coordena e fiscaliza a implementa&ccedil;&atilde;o das decis&otilde;es no n&iacute;vel Estado-Maior Geral e Especial, enquanto os Chefes de Se&ccedil;&atilde;o e Comandantes de Subunidades coordenam e fiscalizam a execu&ccedil;&atilde;o das decis&otilde;es nos respectivos n&iacute;veis de atua&ccedil;&atilde;o e dentro de suas esferas de compet&ecirc;ncias.<br />&nbsp;&nbsp; O Gerenciador de Projetos GP WEB &eacute; a ferramenta amplamente utilizada na fase de implementa&ccedil;&atilde;o das decis&otilde;es.<br />&nbsp; A implementa&ccedil;&atilde;o ocorre, tamb&eacute;m, pela adequa&ccedil;&atilde;o e atualiza&ccedil;&atilde;o (melhoria) dos Procedimentos Operacionais Padr&atilde;o-POP, atualiza&ccedil;&atilde;o dos processos e confec&ccedil;&atilde;o de projetos.</p>',1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,0,NULL,0,NULL),
  (6,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p style=\"text-align: justify;\">Desde sua cria&ccedil;&atilde;o (1976), todos os resultados organizacionais geram um relat&oacute;rio, que &eacute; enviado ao Escal&atilde;o Superior.<br />&nbsp; Cada se&ccedil;&atilde;o do EM possui um calend&aacute;rio de envio de documentos, atrav&eacute;s dos quais o Escal&atilde;o Superior toma conhecimento dos resultados organizacionais da Organização, como relat&oacute;rio de instru&ccedil;&atilde;o e de disponibilidade de material.<br />&nbsp;&nbsp; A IIQ da Organização, ao seu final, sofre uma inspe&ccedil;&atilde;o de comando, planejada pelo S/3 e realizada pelas SU. O relat&oacute;rio da inspe&ccedil;&atilde;o &eacute; encaminhado ao Escal&atilde;o Superior.<br />&nbsp;&nbsp; A Organização tamb&eacute;m presta contas de seus atos e resultados alcan&ccedil;ados, por interm&eacute;dio de exposi&ccedil;&otilde;es do Cmt, durante as inspe&ccedil;&otilde;es de comando do Comando Militar do Sul.<br />&nbsp;&nbsp; Anualmente ap&oacute;s a analise dos relat&oacute;rios apresentados, o Escal&atilde;o Superior divulga suas diretrizes e orienta&ccedil;&otilde;es, assim s&atilde;o realizadas adequa&ccedil;&otilde;es e melhorias na execu&ccedil;&atilde;o das miss&otilde;es pela Organização.</p>',1,NULL,0,NULL,1,NULL,1,NULL,0,NULL,1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,0,NULL,1,NULL),
  (7,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p style=\"text-align: justify;\">Os Princ&iacute;pios, Valores e Cren&ccedil;as (PVC) da Organização s&atilde;o estabelecidos com base nos PVC do Escal&atilde;o Superior e do Ex&eacute;rcito Brasileiro e de acordo com a natureza espec&iacute;fica da tropa de Comunica&ccedil;&otilde;es. Os PVC relacionados a seguir constam do Plano de Gest&atilde;o da Organização .</p><ol><li style=\"text-align:justify;\"><strong>Patriotismo:</strong>amar a P&aacute;tria, a Hist&oacute;ria do Pa&iacute;s, S&iacute;mbolos, Tradi&ccedil;&otilde;es e Na&ccedil;&atilde;o - sublimando a determina&ccedil;&atilde;o de defender seus interesses vitais com o sacrif&iacute;cio da pr&oacute;pria vida.</li><li style=\"text-align:justify;\"><strong>Dever:</strong>cumprir a legisla&ccedil;&atilde;o e a regulamenta&ccedil;&atilde;o a que estiver submetido, com autoridade, determina&ccedil;&atilde;o, dignidade e dedica&ccedil;&atilde;o al&eacute;m do dever, assumindo a responsabilidade pelas decis&otilde;es que tomar.</li><li style=\"text-align:justify;\"><strong>Lealdade:</strong>cultuar a verdade, sinceridade e sadia camaradagem, mantendo-se fiel aos compromissos assumidos.</li><li style=\"text-align:justify;\"><strong>Probidade:</strong>pautar a vida, como soldado e cidad&atilde;o, pela honradez, honestidade e pelo senso de justi&ccedil;a.</li><li style=\"text-align:justify;\"><strong>Coragem</strong>: ter a capacidade de decidir e a iniciativa de implementar a decis&atilde;o, mesmo com o risco de vida ou de interesses pessoais, no intuito de cumprir o dever, assumindo a responsabilidade por sua atitude.</li><li style=\"text-align:justify;\"><strong>Iniciativa:</strong>capacidade para agir, de forma adequada e oportuna, sem depender de ordem ou decis&atilde;o superior.</li><li style=\"text-align:justify;\"><strong>Comprometimento:</strong>com os valores do Estado, da Sociedade e do Ex&eacute;rcito.</li><li style=\"text-align:justify;\"><strong>Lideran&ccedil;a:</strong>capacidade de conduzir seus subordinados pelo exemplo a cumprir miss&otilde;es, em todos os n&iacute;veis de comando e chefia, com efici&ecirc;ncia, motiva&ccedil;&atilde;o e responsabilidade.</li><li style=\"text-align:justify;\"><strong>Objetividade:</strong>ter a capacidade de planejar, coordenar e controlar de maneira clara, objetiva e pr&oacute;-ativa.</li><li style=\"text-align:justify;\"><strong>Legalidade:</strong>obedecer estritamente a lei, pois nenhum resultado poder&aacute; ser considerado bom e nenhuma gest&atilde;o reconhecida como de excel&ecirc;ncia &agrave; revelia da lei</li><li style=\"text-align:justify;\"><strong>Moralidade:</strong>pautar a gest&atilde;o p&uacute;blica por uma conduta moralizada.</li><li style=\"text-align:justify;\"><strong>Efici&ecirc;ncia:</strong>fazer o que precisa ser feito com o m&aacute;ximo de qualidade ao menor custo e tempo poss&iacute;vel.</li><li style=\"text-align:justify;\"><strong>Solidariedade:</strong>rela&ccedil;&atilde;o de responsabilidade entre pessoas unidas por interesses comuns, de maneira que cada elemento do grupo se sinta na obriga&ccedil;&atilde;o moral de apoiar o (s) outro (s).</li><li style=\"text-align:justify;\"><strong>Respeito:</strong>ato ou efeito de respeitar, tratar com defer&ecirc;ncia, considera&ccedil;&atilde;o, apre&ccedil;o e import&acirc;ncia.</li><li style=\"text-align:justify;\"><strong>Confian&ccedil;a:</strong>gozar de bom conceito e cr&eacute;dito, convic&ccedil;&atilde;o do pr&oacute;prio valor e firmeza de &acirc;nimo.</li><li style=\"text-align:justify;\"><strong>Responsabilidade:</strong>cumprir, com corre&ccedil;&atilde;o, ordens, obriga&ccedil;&otilde;es e compromissos, e assumir as conseq&uuml;&ecirc;ncias de seus atos, mesmo que isso lhe cause preju&iacute;zos.</li><li style=\"text-align:justify;\"><strong>Disciplina Consciente:</strong>obriga&ccedil;&atilde;o de fazer ou deixar de fazer alguma coisa, imposta por lei, pela moral, pelos usos e costumes ou pela pr&oacute;pria consci&ecirc;ncia..</li></ol>',1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,1,NULL,0,NULL,1,NULL,1,NULL,0,NULL,0,NULL,0,NULL),
  (8,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p style=\"text-align: justify;\">Desde 2006 anualmente, na reuni&atilde;o que trata do Plano de Gest&atilde;o,onde participa o Comandante da Organização, EM e comandantes de Subunidades, s&atilde;o analisados, tamb&eacute;m, os Princ&iacute;pios, Valores e Cren&ccedil;as.<br />&nbsp;&nbsp; Para efeito de retifica&ccedil;&atilde;o/ratifica&ccedil;&atilde;o dos PVC, s&atilde;o levados em considera&ccedil;&atilde;o as Diretrizes do Escal&atilde;o Superior, as Diretrizes do Dirertor da Organização, a atividades espec&iacute;ficas de Comunica&ccedil;&otilde;es e as observa&ccedil;&otilde;es colhidas pelo Dirertor e EM da Organização, relativas aos Pontos Fortes e as Defici&ecirc;ncias da Organização no cumprimento das diversas miss&otilde;es.</p>',1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,0,NULL,0,NULL),
  (9,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p style=\"text-align: justify;\">Desde de 2006 os Princ&iacute;pios da Administra&ccedil;&atilde;o P&uacute;blica, entendidos como sendo Legalidade, Moralidade, Impessoalidade, Publicidade e Efici&ecirc;ncia, se confundem com os Valores Militares, Institucionais e Organizacionais, amplamente cultuados pelos integrantes do Ex&eacute;rcito Brasileiro.<br />&nbsp;&nbsp; Na Organização a dissemina&ccedil;&atilde;o dos valores e das diretrizes da Administra&ccedil;&atilde;o P&uacute;blica &eacute; feita por meio de instru&ccedil;&otilde;es tanto para os quadros de carreira como para o efetivo tempor&aacute;rio, reuni&otilde;es, formaturas e documentos nos quais s&atilde;o ressaltados aspectos relativos &agrave; legalidade, impessoalidade, moralidade e publicidade. A efici&ecirc;ncia e efic&aacute;cia s&atilde;o uma busca constante em todas as atividades realizadas pela Organização.<br />&nbsp;&nbsp; Os valores e diretrizes da administra&ccedil;&atilde;o p&uacute;blica s&atilde;o internalizados atrav&eacute;s do cumprimento das diretrizes e observ&acirc;ncia dos valores nas atividades di&aacute;rias, e pela a&ccedil;&atilde;o de comando fiscalizadora do EM sobre os respons&aacute;veis pela execu&ccedil;&atilde;o das tarefas.<br />&nbsp;&nbsp; Anualmente, desde 2007, a Organização realiza o Simp&oacute;sio de Administra&ccedil;&atilde;o para o efetivo profissional, no qual s&atilde;o enfatizados os Princ&iacute;pios da Administra&ccedil;&atilde;o P&uacute;blica. O fiel cumprimento das Leis e Regulamentos exemplifica a utiliza&ccedil;&atilde;o desses princ&iacute;pios no dia-a-dia dos integrantes da Unidade.</p>',1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,1,NULL,1,NULL,0,NULL,0,NULL,0,NULL,0,NULL,0,NULL),
  (10,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p style=\"text-align: justify;\">O Comando do Batalh&atilde;o tem sido enf&aacute;tico, nas reuni&otilde;es de Comando do CMS realizadas semanalmente, fazendo gest&otilde;es junto ao Escal&atilde;o Superior e Escal&atilde;o de Apoio na busca de descentraliza&ccedil;&atilde;o / suplementa&ccedil;&atilde;o de Recursos, recompletamento do quadro de pessoal e material.<br />&nbsp;&nbsp; Desde sua cria&ccedil;&atilde;o (1976), na Organização, sempre que existe a oportunidade, o Cmt e/ou EM buscam novas oportunidades, realizando o contato formal, ou n&atilde;o, com militares de outras Organização, com servidores ou &oacute;rg&atilde;os p&uacute;blicos externos ao Ex&eacute;rcito Brasileiro, assim como contatos com &oacute;rg&atilde;os privados, que possam oferecer experi&ecirc;ncias, meios ou recursos que se prestem ao aperfei&ccedil;oamento do pessoal e &agrave; execu&ccedil;&atilde;o das atividades da Organização,&nbsp; sempre visando o melhor cumprimento das miss&otilde;es.<br />&nbsp;&nbsp; Nos &uacute;ltimos dois anos, a Organização apresentou ao Escal&atilde;o Superior projetos para reativa&ccedil;&atilde;o e revitaliza&ccedil;&atilde;o, respectivamente, do Sistema de Comunica&ccedil;&otilde;es por Sat&eacute;lite e do Sistema de Comunica&ccedil;&otilde;es de &Aacute;rea, fundamentais para viabilizar o cumprimento da miss&atilde;o da Organização.</p>',1,NULL,0,NULL,0,NULL,1,NULL,0,NULL,1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,0,NULL,0,NULL),
  (11,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p style=\"text-align:justify;\">Desde sua cria&ccedil;&atilde;o, em 1976, a a&ccedil;&atilde;o fiscalizadora &eacute; exercida a partir do Comandante e ocorre em todos os n&iacute;veis de comando.<br />Permite o acompanhamento e verifica&ccedil;&atilde;o da aplica&ccedil;&atilde;o dos valores, pela conduta &eacute;tica apresentada pelos integrantes do Batalh&atilde;o, tanto profissional quanto pessoal, e pela puni&ccedil;&atilde;o nos desvios de conduta, independente de posto ou gradua&ccedil;&atilde;o do militar.<br />A verifica&ccedil;&atilde;o ocorre tamb&eacute;m, diariamente, nas atividades de preparo, emprego e de apoio, buscando desenvolver em cada militar uma conduta moral e eticamente correta e ao comprometimento de cada militar com a sua miss&atilde;o, com o Batalh&atilde;o e com o Ex&eacute;rcito.</p>',1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,1,NULL,1,NULL,1,NULL,0,NULL,0,NULL,0,NULL,0,NULL),
  (12,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p style=\"text-align: justify;\">Os principais padr&otilde;es de trabalho, que orientam as atividades internas e externas s&atilde;o estabelecidos pelas diretrizes do Escal&atilde;o Superior e pelas NGA e pela diretriz do Cmt da Organização, fixadas desde a assun&ccedil;&atilde;o do comando; pelos regulamentos e normas militares; pelo plano de gest&atilde;o da Organização e atrav&eacute;s de ordens espec&iacute;ficas, de acordo com a necessidade, que s&atilde;o publicadas em Boletim Interno. As rotinas s&atilde;o consolidadas nos Procedimentos Operacionais Padr&atilde;o (POP). Os padr&otilde;es de trabalho seguem os crit&eacute;rios necess&aacute;rios para se obter a efici&ecirc;ncia e a efic&aacute;cia dos processos e s&atilde;o de responsabilidade do Cmt da Organização e do EM.</p>',1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,1,NULL,1,NULL,1,NULL,0,NULL,0,NULL,0,NULL,0,NULL),
  (13,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p style=\"text-align: justify;\">O cumprimento dos padr&otilde;es de trabalho e regras de funcionamento das atividades da Organização &eacute; verificado atrav&eacute;s de inspe&ccedil;&otilde;es programadas e inopinadas, realizadas por todos os comandantes e em todos os n&iacute;veis,&nbsp; e ainda&nbsp; pela rotina di&aacute;ria da caserna,&nbsp; di&aacute;logos e reuni&otilde;es entre os oficiais e pra&ccedil;as da Organização.</p>',1,NULL,1,NULL,1,NULL,0,NULL,0,NULL,1,NULL,1,NULL,0,NULL,0,NULL,0,NULL,0,NULL,0,NULL),
  (14,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p style=\"text-align: justify;\">Desde 2006, os principais ID utilizados s&atilde;o:<br />&nbsp;&nbsp; - &Iacute;ndice de sufici&ecirc;ncia no Teste de Aptid&atilde;o F&iacute;sica - TAF, do Efetivo Profisasional<br />&nbsp;&nbsp; - &Iacute;ndice de sufici&ecirc;ncia no Teste de Aptid&atilde;o F&iacute;sica - TAF, do Efetivo Vari&aacute;vel<br />&nbsp;&nbsp; - Pontua&ccedil;&atilde;o obtida no Sistema de Avalia&ccedil;&atilde;o do COTer - SISTAVOP<br />&nbsp;&nbsp; - &Iacute;ndice de satisfa&ccedil;&atilde;o no fornecimento de material<br />&nbsp;&nbsp; - &Iacute;ndice de implementa&ccedil;&atilde;o dos Planos de A&ccedil;&atilde;o do Plano de Gest&atilde;o (desde 2006)<br />&nbsp;&nbsp; - Resultados da Campanha do Agasalho<br />&nbsp;&nbsp; - Resultados da Campanha do Quilo<br />&nbsp;&nbsp; - Consumo mensal de &aacute;gua<br />&nbsp;&nbsp; - Consumo mensal de energia el&eacute;trica<br />&nbsp;&nbsp; - Gasto mensal com conta telef&ocirc;nica<br />&nbsp;&nbsp; - Percentual de processos mapeados<br />&nbsp;&nbsp; - Nr de processos licitat&oacute;rios realizados<br />&nbsp;&nbsp; - Consumo de &oacute;leo diesel em litros<br />&nbsp;&nbsp; - Consumo de Gasolina em litros<br />&nbsp;&nbsp; - Disponibilidade de muni&ccedil;&atilde;o<br />&nbsp;&nbsp; - Disponibilidade de Armamento<br />&nbsp;&nbsp; - Disponibilidade de Viaturas<br />&nbsp;&nbsp; - Percentual de sufici&ecirc;ncia no TAT do EP<br />&nbsp;&nbsp; - &Iacute;ndice de satisfa&ccedil;&atilde;o no atendimento m&eacute;dico<br />&nbsp;&nbsp; - &Iacute;ndice de satisfa&ccedil;&atilde;o no atendimento odontol&oacute;gico<br />&nbsp;&nbsp; - Nr de acidentes em servi&ccedil;o<br />&nbsp;&nbsp; - &Iacute;ndice de satisfa&ccedil;&atilde;o na qualidade das refei&ccedil;&otilde;es<br />&nbsp;&nbsp; - Percentual de militares que recebem o aux&iacute;lio-transporte<br />&nbsp;&nbsp; - Percentual de militares que conclu&iacute;ram o CFC com aproveitamento<br />&nbsp;&nbsp; - Percentual de militares capacitados no CFSD<br />&nbsp;&nbsp; - Nr de militares formados em cursos e est&aacute;gios ministrados pelo Organização<br />&nbsp;&nbsp; - &Iacute;ndice da Certifica&ccedil;&atilde;o Log&iacute;stica</p>',1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,0,NULL,1,NULL),
  (15,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p style=\"text-align: justify;\">Desde sua cria&ccedil;&atilde;o (1976), de acordo com as demandas do Batalh&atilde;o, o Comando (Cmt e Estado-Maior), t&ecirc;m buscado novas oportunidades pelo contato formal ou n&atilde;o, com militares de outras Organização, com servidores ou &oacute;rg&atilde;os p&uacute;blicos externos ao Ex&eacute;rcito Brasileiro, nas reuni&otilde;es ou solenidades semanais e mensais.<br />&nbsp;&nbsp; A parceria e forma&ccedil;&atilde;o de for&ccedil;as-tarefas com outros &oacute;rg&atilde;os, devidamente autorizadas e em conson&acirc;ncia com as diretrizes do Escal&atilde;o Superior,&nbsp; tem mostrado uma maior efici&ecirc;ncia e alcance na execu&ccedil;&atilde;o de projetos.<br />&nbsp;&nbsp;</p>',1,NULL,0,NULL,1,NULL,1,NULL,0,NULL,1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,0,NULL,0,NULL),
  (16,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p style=\"text-align: justify;\">Desde 2006, a dissemina&ccedil;&atilde;o dos valores ocorre, entre outras maneiras, atrav&eacute;s das instru&ccedil;&otilde;es abaixo:<br />&nbsp;&nbsp; - Tempos de Instru&ccedil;&atilde;o destinados aos Atributos da &Aacute;rea Afetiva (para o Efetivo Vari&aacute;vel);<br />&nbsp;&nbsp; - Instru&ccedil;&otilde;es de Quadros para Oficiais e Sargentos.</p><p style=\"text-align: justify;\">Esses Princ&iacute;pios, Valores e Cren&ccedil;as - PVC do Batalh&atilde;o est&atilde;o descritos no Plano de Gest&atilde;o e est&atilde;o alinhados aos PVC do Escal&atilde;o Superior e do Ex&eacute;rcito Brasileiro.</p>',1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,0,NULL,0,NULL),
  (17,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p style=\"text-align: justify;\">Desde sua cria&ccedil;&atilde;o, em 1976, os valores da Organização s&atilde;o utilizados pelos integrantes da Organização em todas as atividades de servi&ccedil;o, pois se confundem com os valores morais e com o perfil do militar.<br />&nbsp;&nbsp; Diariamente, principalmente em atividades coletivas, os valores da Organização s&atilde;o exaltados pelos comandantes nos diversos n&iacute;veis.</p>',1,NULL,0,NULL,1,NULL,1,NULL,0,NULL,1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,0,NULL,0,NULL),
  (18,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p style=\"text-align: justify;\">Desde sua cria&ccedil;&atilde;o (1976), o Comando da Organização verifica o conhecimento dos valores pelos integrantes da Organização, diretamente, atrav&eacute;s:<br />&nbsp;&nbsp; - Nos exerc&iacute;cios de campanha, pela observa&ccedil;&atilde;o direta da tropa empenhada.<br />&nbsp;&nbsp; - No trato di&aacute;rio com seus superiores, pela observa&ccedil;&atilde;o direta nas reuni&otilde;es e nos despachos com o comandante da Organização.<br />&nbsp;&nbsp; - Nas avalia&ccedil;&otilde;es semestrais dos ST/Sgt, pelos resultados da &aacute;rea afetiva.<br />&nbsp; - Nas visitas e inspe&ccedil;&otilde;es de comando &agrave;s SU e Se&ccedil;&otilde;es, que s&atilde;o realizadas mensalmente, pelas conversas com os&nbsp; auxiliares (ST/Sgt).<br />&nbsp;&nbsp; - No TFM, formaturas, solenidades e reuni&otilde;es sociais pela observa&ccedil;&atilde;o direta.<br />&nbsp;&nbsp; - Nas instru&ccedil;&otilde;es de Atributos da &Aacute;rea Afetiva na IIB e na CTTEP, verificando o atingimento dos objetivos da instru&ccedil;&atilde;o.<br />&nbsp;&nbsp; Essas a&ccedil;&otilde;es v&ecirc;m sendo executadas h&aacute; mais de tr&ecirc;s anos.</p>',1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,0,NULL,0,NULL),
  (19,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p style=\"text-align: justify;\">Desde sua cria&ccedil;&atilde;o (1976), o Dirertor do Batalh&atilde;o analisa diariamente seu desempenho de acordo com o cumprimento dos prazos e rotinas de trabalho, de acordo com as OI e OSv e com as respectivas APA e relat&oacute;rios, quando participa de um exercicio, seja interno ou externo, sendo poss&iacute;vel identificar oportunidades de melhorias e definir medidas corretivas a serem implementadas.<br />&nbsp;&nbsp; Utilizando-se o Gerenciador de Projetos GP WEB, s&atilde;o controlados os Indicadores de Desempenho preconizados pelo Sistema de Excel&ecirc;ncia Gerencial, que servem de par&acirc;metro para an&aacute;lise.<br />&nbsp;&nbsp; O Comando da Organização efetua sistematicamente inspe&ccedil;&otilde;es para verificar o andamento dos processos dos diferentes setores, como inspe&ccedil;&otilde;es na instru&ccedil;&atilde;o, nos acampamentos, em exerc&iacute;cios operacionais, inspe&ccedil;&otilde;es agendadas em todas as classes de material, incluindo a verifica&ccedil;&atilde;o de seus processos.<br />&nbsp;&nbsp; Na sistem&aacute;tica de an&aacute;lise insere-se a verifica&ccedil;&atilde;o, por parte do Cmt, do cumprimento de prazos e a qualidade dos documentos produzidos pelas se&ccedil;&otilde;es do EM Geral e Especial.</p>',1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,1,NULL,1,NULL,1,NULL,0,NULL,0,NULL,0,NULL,1,NULL),
  (20,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p style=\"text-align: justify;\">Desde 2006, o Batalh&atilde;o vem introduzindo gradualmente em suas instru&ccedil;&otilde;es e em seus cursos e est&aacute;gios, os conceitos relacionados ao Sistema de Excel&ecirc;ncia do Ex&eacute;rcito Brasileiro, procurando desenvolver no seu p&uacute;blico interno a vis&atilde;o sist&ecirc;mica, a abordagem por processos, a internaliza&ccedil;&atilde;o do conhecimento organizacional, a medi&ccedil;&atilde;o do desempenho organizacional, a sistem&aacute;tica de trabalho e o foco no cliente e nos resultados.</p><p style=\"text-align: justify;\">A CTTEP e o Comando da Organização contemplam os planos relacionados &agrave; cultura da Excel&ecirc;ncia, definidos pelo Sistema de Excel&ecirc;ncia - Ex&eacute;rcito Brasileiro SE-EB, com a realiza&ccedil;&atilde;o de treinamentos anuais de forma presencial ou EAD, por meio de ambiente virtual e distribui&ccedil;&atilde;o de Manuais T&eacute;cnicos, denominados Instru&ccedil;&otilde;es Provis&oacute;rias (IP) e CD ROM auto-execut&aacute;veis, a seus integrantes, abrangendo os temas como: PEO, SMDO, AMP, MEG e PMC.</p><p style=\"text-align: justify;\">Indiretamente, todos os militares s&atilde;o alcan&ccedil;ados, pois os conhecimentos adquiridos pelos militares que realizam os cursos s&atilde;o internalizados e incorporados &agrave;s rotinas de trabalho atrav&eacute;s de instru&ccedil;&otilde;es mensais de dissemina&ccedil;&atilde;o desses conhecimentos adquiridos.</p><p style=\"text-align: justify;\">Nos &uacute;ltimos tr&ecirc;s anos integrantes da Organização s&atilde;o designados para participarem de cursos presenciais em &oacute;rg&atilde;o de Gest&atilde;o Estadual e Federal (PGQP e GESPUBLICA).</p>',1,NULL,1,NULL,1,NULL,0,NULL,0,NULL,1,NULL,1,NULL,1,NULL,0,NULL,0,NULL,0,NULL,1,NULL),
  (21,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p style=\"text-align: justify;\">Os comandantes de fra&ccedil;&atilde;o, em todos os n&iacute;veis s&atilde;o orientados a estimular os seus subordinados a buscar o aperfei&ccedil;oamento, seja referente aos cursos e est&aacute;gios no Ex&eacute;rcito ou na busca de oportunidades em outras institui&ccedil;&otilde;es, seja para a conclus&atilde;o dos cursos regulares do ensino fundamental, m&eacute;dio ou superior.</p><p style=\"text-align: justify;\">Os principais m&eacute;todos utilizados para implementar o aprendizado s&atilde;o as instru&ccedil;&otilde;es de quadros, as An&aacute;lises P&oacute;s-A&ccedil;&atilde;o, os cursos e os est&aacute;gios realizados na pr&oacute;pria Organização ou em Unidades militares da Guarni&ccedil;&atilde;o ou, ainda, em institui&ccedil;&otilde;es civis. O militar capacitado nos cursos ou est&aacute;gios atua como um multiplicador do conhecimento dentro da Organização.</p><p style=\"text-align: justify;\">Pode-se citar como melhorias implementadas a reforma da cozinha e no refeit&oacute;rio de cabos e soldados, a reforma da c&acirc;mara fria, a reforma do alojamento e banheiro das companhias,a reforma do Galp&atilde;o Crioulo (ambiente de confraterniza&ccedil;&atilde;o), computadores mais modernos para todas as se&ccedil;&otilde;es, estrutura&ccedil;&atilde;o e cabeamento de toda a rede de internet, melhoria da disponibilidade das Vtr operacionais e administrativas do Batalh&atilde;o (mantendo-se acima dos 80%).</p>',1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,0,NULL,0,NULL),
  (22,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p style=\"text-align: justify;\">O Batalh&atilde;o analisa seu desempenho de acordo com o cumprimento dos prazos e rotinas de trabalho, de acordo com as OI e OSv e com as respectivas APA e relat&oacute;rios &eacute; poss&iacute;vel identificar oportunidades de melhorias e definir medidas corretivas a serem implementadas.</p>',1,NULL,1,NULL,1,NULL,0,NULL,0,NULL,1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,0,NULL,1,NULL),
  (23,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p style=\"text-align: justify;\">Desde 2006, por meio da auto-avalia&ccedil;&atilde;o e respectiva valida&ccedil;&atilde;o, bem como por meio do acompanhamento dos ID contidos no Plano de Gest&atilde;o.<br />&nbsp;&nbsp; De posse da an&aacute;lise feita utilizando o ciclo PDCA, o comando da Organização disp&otilde;e de dados para verificar o desempenho das Se&ccedil;&otilde;es e Subunidades, para melhor distribuir suas miss&otilde;es e realizar as corre&ccedil;&otilde;es necess&aacute;rias para melhorar o desempenho.<br />&nbsp;&nbsp; Os Indicadores de Desempenho permitem avaliar quantitativa e qualitativamente a operacionalidade da Organização, ajudando na tomada de medidas corretivas para melhorar o desempenho de cada Objetivo.<br />&nbsp;&nbsp; Tamb&eacute;m por observa&ccedil;&otilde;es do escal&atilde;o superior e das autoridades visitantes, por meio das respectivas pesquisas, implementadas no &uacute;ltimo ciclo.<br />&nbsp;&nbsp; A pontua&ccedil;&atilde;o anual obtida pela Organização no SISPEGWEB tamb&eacute;m est&aacute; sendo considerada na an&aacute;lise do desempenho.</p>',1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,1,NULL,1,NULL,1,NULL,0,NULL,0,NULL,0,NULL,0,NULL),
  (24,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p style=\"text-align: justify;\">Desde 2006, a necessidade de informa&ccedil;&otilde;es comparativas &eacute; identificada pelo Dirertor e EM da Organização na reuni&atilde;o que trata da defini&ccedil;&atilde;o das estrat&eacute;gias e metas do Plano de Gest&atilde;o e nas atividades di&aacute;rias como par&acirc;metro para defini&ccedil;&atilde;o de metas e medi&ccedil;&atilde;o de desempenho.</p><p style=\"text-align: justify;\">Al&eacute;m disso, as avalia&ccedil;&otilde;es realizadas na Organização (TAF, TAT) durante o ano de instru&ccedil;&atilde;o, s&atilde;o comparadas com as metas do escal&atilde;o superior (Diretriz de Instru&ccedil;&atilde;o do COTER e do Comando Militar do Sul).</p>',1,NULL,0,NULL,0,NULL,1,NULL,0,NULL,1,NULL,1,NULL,1,NULL,0,NULL,0,NULL,0,NULL,0,NULL),
  (25,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p style=\"text-align: justify;\">\n&nbsp;&nbsp; Por meio de reuni&otilde;es, o comando e o EM analisa os Objetivos Estrat&eacute;gicos definidos de acordo com a conjuntura atual, buscando atualiz&aacute;-los de acordo com as necessidades. As estrat&eacute;gias s&atilde;o avaliadas de acordo com a sua efici&ecirc;ncia e efic&aacute;cia, utilizando-se indicadores de desempenho qualitativos e quantitativos. Para cada Objetivo Estrat&eacute;gico Organizacional s&atilde;o estabelecidos Fatores Cr&iacute;ticos de Sucesso (FCS). Para cada estrat&eacute;gia s&atilde;o previstas metas de curto, m&eacute;dio e longo prazo. Para cada meta das estrat&eacute;gias s&atilde;o estabelecidos indicadores de desempenho e a elabora&ccedil;&atilde;o dos Planos de A&ccedil;&atilde;o (5W2H) visando alcan&ccedil;ar as metas estabelecidas.</p>\n<p style=\"text-align: justify;\">\n&nbsp;&nbsp; A execu&ccedil;&atilde;o dos Planos de A&ccedil;&atilde;o e seu acompanhamento por interm&eacute;dio de ID, permite a avalia&ccedil;&atilde;o de cada Objetivo Estrat&eacute;gico Organizacional e das estrat&eacute;gias estabelecidas para alcan&ccedil;&aacute;-los.</p>\n',1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,0,NULL,0,NULL),
  (26,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p style=\"text-align: justify;\">Na reuni&atilde;o que trata do Plano de Gest&atilde;o, o comando e o EM analisa os Objetivos Organizacionais (OO) definidos de acordo com a conjuntura atual e as necessidades da Organização. O desempenho &eacute; verificado por meio dos Indicadores de Desempenho apresentados e &eacute; feita a an&aacute;lise sobre o alcance de cada OO em rela&ccedil;&atilde;o &agrave; Miss&atilde;o e a Vis&atilde;o de Futuro do Batalh&atilde;o.</p>',1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,0,NULL,1,NULL),
  (27,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p style=\"text-align: justify;\">Desde de sua cria&ccedil;&atilde;o em 1976, as principais decis&otilde;es s&atilde;o comunicadas pessoalmente pelo Cmt atrav&eacute;s de reuni&otilde;es semanais com todos os oficiais, em reuni&otilde;es di&aacute;rias com seu EM Geral e Especial e Cmt SU, e aos integrantes da Organização em formaturas realizadas semanalmente, ou atrav&eacute;s de documentos expedidos, da seguinte forma:<br />&nbsp;&nbsp; 1) Comunica&ccedil;&atilde;o eletr&ocirc;nica on-line direta ao interessado, usando o Gerenciador de Mensagens e Projetos GP WEB, adotado desde 2008 como principal ferramenta de gerenciamento.<br />&nbsp;&nbsp; 2) publica&ccedil;&atilde;o em Boletim Interno e Boletim Administrativo da Organização, quando a decis&atilde;o &eacute; um assunto de car&aacute;ter geral para os militares da Organização;<br />&nbsp;&nbsp; 3) Boletim Reservado, para assuntos com classifica&ccedil;&atilde;o reservada ou sigilosa;<br />&nbsp;&nbsp; 4) Ordem de Servi&ccedil;o, quando a decis&atilde;o tomada gera um conjunto de procedimentos e emprego de pessoal e material em<br />miss&otilde;es diversas, que n&atilde;o estejam relacionadas diretamente com a atividade fim;<br />&nbsp;&nbsp; 5) Ordem de Instru&ccedil;&atilde;o, quando a decis&atilde;o tomada gera um conjunto de procedimentos com emprego de tropa em atividades operacionais de preparo e emprego;<br />&nbsp;&nbsp; 6) Diretrizes de Comando;<br />&nbsp;&nbsp; 7) Quadro de Trabalho Semanal - QTS ;<br />&nbsp;&nbsp; 8) partes, memorandos, of&iacute;cios,<br />&nbsp;&nbsp; 9) a utiliza&ccedil;&atilde;o da Intranet tem facilitado a comunica&ccedil;&atilde;o das principais decis&otilde;es, seja atrav&eacute;s de despacho de mensagens ou da aprova&ccedil;&atilde;o de documentos que re&uacute;nem ordens e procedimentos.</p>',1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,1,NULL,1,NULL,1,NULL,1,NULL,0,NULL,0,NULL,0,NULL),
  (28,2015,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'<p>O acompanhamento &eacute; feito atrav&eacute;s dos chefes de se&ccedil;&atilde;o, os quais possuem as seguintes responsabilidades:</p><p>S1 - assuntos relativos aos recursos humanos;</p><p>S2 - assuntos relativos &agrave; intelig&ecirc;ncia e contra-intelig&ecirc;ncia;</p><p>S3 - assuntos referentes ao preparo e ao emprego da tropa;</p><p>S4 - assuntos relativos &agrave; log&iacute;stica.</p><table border=\"0\" cellpadding=\"0\" cellspacing=\"0\"><tbody><tr><td style=\"width: 131px; height: 22px;\"><p style=\"text-align: center;\"><span style=\"font-size: 11px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"line-height: 115%; font-style: normal;\"><span style=\"font-style: normal; font-variant: normal; font-weight: normal; line-height: normal; font-size-adjust: none; font-stretch: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><strong>Pr&aacute;ticas de Gest&atilde;o</strong></span></span></p></td><td style=\"width: 99px; height: 22px;\"><p style=\"text-align: center;\"><span style=\"font-size: 11px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Dissemina&ccedil;&atilde;o</strong></span></span></p></td><td style=\"width: 95px; height: 22px;\"><p style=\"text-align: center;\"><span style=\"font-size: 11px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Continuidade</strong></span></span></p></td><td style=\"width: 327px; height: 22px;\"><p style=\"text-align: center;\"><span style=\"font-size: 11px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Melhorias Implementadas</strong></span></span></p></td></tr><tr><td style=\"width: 131px; height: 27px;\"><p><span style=\"font-size: 11px;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Dissemina&ccedil;&atilde;o dos valores e diretrizes da Adm P&uacute;blica</span></span></p></td><td style=\"width: 99px; height: 27px;\"><p><span