INSERT INTO projeto_viabilidade (projeto_viabilidade_id, projeto_viabilidade_cia, projeto_viabilidade_projeto, projeto_viabilidade_demanda, projeto_viabilidade_nome, projeto_viabilidade_codigo, projeto_viabilidade_necessidade, projeto_viabilidade_alinhamento, projeto_viabilidade_requisitos, projeto_viabilidade_solucoes, projeto_viabilidade_viabilidade_tecnica, projeto_viabilidade_financeira, projeto_viabilidade_institucional, projeto_viabilidade_solucao, projeto_viabilidade_continuidade, projeto_viabilidade_responsavel, projeto_viabilidade_acesso, projeto_viabilidade_cor, projeto_viabilidade_data, projeto_viabilidade_ativo, projeto_viabilidade_viavel) VALUES
  (1,1,1,1,'Quadra coberta','P002/2012','Uma área fechada para prática de exercícios físicos.','Melhora da capacidade operacional.','Projeto básico realizado por uma firma de engenharia. Recursos financeiros do escalão superior. ','Receber o recurso do escalão superio e construir a quadra poliesportiva coberta, ou em caso de contenção de verba deixar toda a documentação técnica e o edital da licitação pronto para o próximo exercício fiscal.','Não há nenhum empecilho técnico, haja vista não ser uma obra de enganharia de grande vulto, sendo a opção mais simples a utilização de quadra poliesportiva pré-fabricada;','A análise inicial orçou o empreendimento em R$120.000,00, o que torna a obra de baixo custo, facilitando a concretização da mesma.','Há um interesse do escalão superior de que uma melhor capacidade física dos integrantes, o que facilita a aceitação do projeto.','Requisitar a verba necessária ao escalão superior e paralelamente realizar a primeira fase da licitação que será a elaboação do edital e enviar para análise jurídica.','A equipe de estudo de viabilidade é de parecer favorável ao prosseguimento dos trabalhos, por haver ampla possibilidade técnica, financeira e forte alinhamente com objetivo estratégico.',7,0,'c3fee4','2016-10-12 09:15:56',1,1);
